INSERT INTO `t_topic` VALUES (9453, 'admin', 1, '“国无常强，无常弱。奉法者强则国强，奉法者弱则国弱。”党的十八届四中全会鲜明地提出“依法治国”这一时代主题，要求全社会要厉行法治，共建法治中国。这就要求行政机关做到（　　）', '[]', '[{\"A\":{\"content\":\"科学立法，民主立法\",\"imageList\":[]}},{\"B\":{\"content\":\"文明执法，依法行政\",\"imageList\":[]}},{\"C\":{\"content\":\"公正司法，依法经营\",\"imageList\":[]}},{\"D\":{\"content\":\"学法尊法，依法办事\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '大埔县', 1608129802216, 1608129802216, 3);
