INSERT INTO `t_topic` VALUES (9502, 'admin', 1, '2019年12月11日是广西壮族自治区成立61周年纪念日。广西由成立之初的一穷二白，发展到如今生产总值增长数百倍，还是我国重要的铝产业生产基地。这是因为（　　）\n①实行民族高度自治制度\n②实行民族区域自治制度\n③一心一意发展壮大旅游业\n④广西资源种类多，储量大', '[]', '[{\"A\":{\"content\":\"①②\",\"imageList\":[]}},{\"B\":{\"content\":\"①③\",\"imageList\":[]}},{\"C\":{\"content\":\"②④\",\"imageList\":[]}},{\"D\":{\"content\":\"③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '南平', 1608129802517, 1608129802517, 3);
