INSERT INTO `t_topic` VALUES (9506, 'admin', 1, '2019年3月28日，西藏自治区各族各界干部群众1万余人在布达拉宫广场隆重集会，热烈庆祝西藏民主改革60周年。60年来，西藏地区国民生产总值增长了191倍，居民人均可支配收入近两万元，人民生活幸福、安居乐业。60年来，西藏地区快速发展得益于（　　）\n①中国共产党的正确领导和一系列民族政策的实施\n②平等团结互助和谐的社会主义新型民族关系的形成和巩固\n③党和国家在人财物等方面对西藏地区经济社会发展的大力支持\n④西藏人民艰苦创业，努力建设自己的家园', '[]', '[{\"A\":{\"content\":\"①②④\",\"imageList\":[]}},{\"B\":{\"content\":\"①②③\",\"imageList\":[]}},{\"C\":{\"content\":\"①②③④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '肥城市', 1608129802547, 1608129802547, 3);
