INSERT INTO `t_topic` VALUES (9518, 'admin', 1, '2019年7月18日电，近日，中共中央总书记、国家主席、中央军委主席习近平对地方人大及其常委会工作作出重要指示强调，县级以上地方人大设立常委会，是发展和完善人民代表大会制度的一个重要举措。在我国，人民代表大会制度（　　）\n①是我国的根本政治制度\n②是人民掌握国家政权、行使权力的根本途径\n③是社会主义民主政治建设的基础\n④能有效的保障人民当家作主的地位', '[]', '[{\"A\":{\"content\":\"①②④\",\"imageList\":[]}},{\"B\":{\"content\":\"①②③\",\"imageList\":[]}},{\"C\":{\"content\":\"②③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②③④\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '兰陵县', 1608129802616, 1608129802616, 3);
