INSERT INTO `t_topic` VALUES (9524, 'admin', 1, '一位西方法学家曾说过：“一次司法不公正胜过十次犯罪，犯罪污染的是水流，而司法不公正污染的是水源。”避免“水源”被污染，需要（　　）\n①人民政府忠实于法律，忠实于社会主义事业，参与司法活动\n②人民法院在司法活动中，必须坚持以事实为根据、以法律为准绳\n③人民法院依法独立公正行使审判权，不受行政机关、社会团体和个人的干涉\n④人民检察院依照法律规定独立行使检察权，不受行政机关、社会团体和个人的干涉', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"②③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '广东', 1608129802649, 1608129802649, 3);
