INSERT INTO `t_topic` VALUES (9534, 'admin', 1, '以下关于人民代表大会和其他国家机关的关系，说法正确的有（　　）\n①国家的行政机关、监察机关、审判机关、检察机关等都由人民代表大会产生\n②人民代表大会授予其他国家机关分别行使行政权、监察权、审判权和检察权\n③政府、监察机关、法院、检察院必须对下级人民代表大会负责，并受它监督\n④人大和政府、监察机关、法院、检察院分工不同，职责、根本目标不同', '[]', '[{\"A\":{\"content\":\"①②\",\"imageList\":[]}},{\"B\":{\"content\":\"②③\",\"imageList\":[]}},{\"C\":{\"content\":\"①④\",\"imageList\":[]}},{\"D\":{\"content\":\"①③\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '江西', 1608129802706, 1608129802706, 3);
