INSERT INTO `t_topic` VALUES (9581, 'admin', 1, '党的十九大把“习近平新时代中国特色社会主义思想”写入党章，作为我们党新的指导思想。在这一新的指导思想中强调坚定道路自信、理论自信、制度自信、文化自信，其中“制度自信”之一就是坚持和完善我国的人民代表大会制度。坚持和完善人民代表大会制度（　　）\n①有利于保证人民当家作主\n②有利于全面依法治国\n③有利于实现中华民族伟大复兴\n④有利于发展我国的基本经济制度', '[]', '[{\"A\":{\"content\":\"①②④\",\"imageList\":[]}},{\"B\":{\"content\":\"②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②③\",\"imageList\":[]}},{\"D\":{\"content\":\"①②③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '镇康县', 1608129802970, 1608129802970, 3);
