INSERT INTO `t_topic` VALUES (9584, 'admin', 1, '村民委员会是中国大陆地区乡（镇）所辖的行政村的村民选举产生的。对此，下列说法正确的是（　　）\n①村民委员会群众性自治组织\n②村民可以通过村民会议等形式，参与本村公共事务和公益事业的决策与管理\n③村民制定自治章程或村规民约，规范村民委员会和村民的行为\n④村民委员会和居民委员会是国家最小的行政机关', '[]', '[{\"A\":{\"content\":\"①③④\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②③\",\"imageList\":[]}},{\"D\":{\"content\":\"①②③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '双台子区', 1608129802988, 1608129802988, 3);
