INSERT INTO `t_topic` VALUES (9592, 'admin', 1, '作为当代的中学生，在感受经济生活巨大变化的同时，也应了解我国的基本经济制度。下列关于我国基本经济制度的说法正确的是（　　）', '[]', '[{\"A\":{\"content\":\"公有制是我国社会主义经济制度的基础，是国民经济的主导力量\",\"imageList\":[]}},{\"B\":{\"content\":\"现阶段我国基本经济制度的确立，是由我国社会主义性质和初级阶段的国情决定的\",\"imageList\":[]}},{\"C\":{\"content\":\"公有制经济和非公有制经济在国民经济中的地位是平等的\",\"imageList\":[]}},{\"D\":{\"content\":\"非公有制经济是社会主义经济的重要组成部分\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '滨海县', 1608129803034, 1608129803034, 3);
INSERT INTO `t_topic` VALUES (9593, 'admin', 1, '下列关于民族区域自治制度的说法中，正确的是（　　）\n①是我国的一项基本政治制度\n②在国家的统一领导下，各少数民族聚居的地方实行区域自治\n③民族自治机关必须服从中央的领导\n④不利于民族地区经济社会的发展和人民生活水平的提高', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"①③④\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '镇康县', 1608129803040, 1608129803040, 3);
INSERT INTO `t_topic` VALUES (9594, 'admin', 1, '国有经济是生产资料属于全体人民共同所有的一种经济形式。下列有关国有经济说法正确的是（　　）', '[]', '[{\"A\":{\"content\":\"国有经经济是社会主义市场经济的唯一组成部分\",\"imageList\":[]}},{\"B\":{\"content\":\"国有经济是国民经济的主导力量\",\"imageList\":[]}},{\"C\":{\"content\":\"国有经济是社会主义经济制度的基础\",\"imageList\":[]}},{\"D\":{\"content\":\"国有经济是国民经济的主体\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '化德县', 1608129803046, 1608129803046, 3);
INSERT INTO `t_topic` VALUES (9595, 'admin', 1, '下列对于人民检察院的认识中，不正确的是（　　）', '[]', '[{\"A\":{\"content\":\"人民检察院依照法律规定独立行使检察权\",\"imageList\":[]}},{\"B\":{\"content\":\"人民检察院对国家机关及其工作人员的违法行为实行监督\",\"imageList\":[]}},{\"C\":{\"content\":\"人民检察院对国家机关及其工作人员的违法行为实行监督\",\"imageList\":[]}},{\"D\":{\"content\":\"人民检察院对刑事犯罪行为代表国家提起诉讼\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '梅里斯区', 1608129803052, 1608129803052, 3);
INSERT INTO `t_topic` VALUES (9596, 'admin', 1, '2018年《政府工作报告》指出，坚持权利平等、机会平等、规则平等，认真解决民营企业反映的突出问题，让民营企业在市场经济中尽显身手。这表明（　　）', '[]', '[{\"A\":{\"content\":\"民营企业对经济发展起主导作用\",\"imageList\":[]}},{\"B\":{\"content\":\"我国鼓励、支持非公有制经济发展\",\"imageList\":[]}},{\"C\":{\"content\":\"非公有制经济控制着我国的经济命脉\",\"imageList\":[]}},{\"D\":{\"content\":\"非公有制经济是社会主义经济制度的基础\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '武功县', 1608129803058, 1608129803058, 3);
INSERT INTO `t_topic` VALUES (9597, 'admin', 1, '关于非公有制经济，下列说法正确的是（　　）\n①包括集体经济、个体经济、私营经济等\n②在支撑经济增长、增加税收、扩大就业、促进大众创业和万众创新等方面发挥着重要作用\n③国家鼓励、支持、引导非公有制的发展\n④在我国，公有制经济与非公有制经济都是社会主义市场经济的重要组成部分，是我国经济社会发展的重要基础', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '新沂市', 1608129803064, 1608129803064, 3);
INSERT INTO `t_topic` VALUES (9598, 'admin', 1, '对下列宪法条文的认识正确的是（　　）', '[\"http://static.lzxtedu.com/8b2/3205.png\"]', '[{\"A\":{\"content\":\"宪法是人民意志的集中体现\",\"imageList\":[]}},{\"B\":{\"content\":\"人民法院在我国的国家机构中居主导地位\",\"imageList\":[]}},{\"C\":{\"content\":\"宪法是适用于国家全体公民\",\"imageList\":[]}},{\"D\":{\"content\":\"有利于保障公民权利、捍卫社会公平正义\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '新沂市', 1608129803069, 1608129803069, 3);
INSERT INTO `t_topic` VALUES (9599, 'admin', 1, '在我国，人民代表大会制度的基本内容包括（　　）\n①人大代表是国家的主人，国家的一切权力属于人大代表\n②人民通过民主选举选出代表，组成各级人民代表大会作为国家权力机关\n③由人民代表大会产生国家行政机关、审判机关、检察机关、监察机关\n④实行民主集中制，重大问题经人民代表大会充分讨论，遵循少数服从多数原则', '[]', '[{\"A\":{\"content\":\"②③④\",\"imageList\":[]}},{\"B\":{\"content\":\"①②③\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②④\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '化德县', 1608129803075, 1608129803075, 3);
INSERT INTO `t_topic` VALUES (9600, 'admin', 1, '人大代表是有人民选举产生，要代表人民的利益和意志，受人民监督。我国人大代表的职责是（　　）\n①依照宪法和法律赋予本级人民代表大会的各项职权，参加行使国家权力\n②依法审议各项议案和报告、表决各项决定、提出议案和质询案\n③必须与人民群众保持密切联系，听取和反映人民群众的意见和要求\n④独立行使立法权，做到严格执法，公正司法', '[]', '[{\"A\":{\"content\":\"①②④\",\"imageList\":[]}},{\"B\":{\"content\":\"①②③\",\"imageList\":[]}},{\"C\":{\"content\":\"②③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①③④\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '化德县', 1608129803080, 1608129803080, 3);
INSERT INTO `t_topic` VALUES (9601, 'admin', 1, '中华人民共和国主席（　　）\n①是中华人民共和国的国家元首\n②是代表中华人民共和国的国家机关\n③代表中华人民共和国行使人民代表大会赋予的职权\n④每届任期同全国人民代表大会每届任期不同', '[]', '[{\"A\":{\"content\":\"①④\",\"imageList\":[]}},{\"B\":{\"content\":\"③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②\",\"imageList\":[]}},{\"D\":{\"content\":\"②③\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '南岸区', 1608129803085, 1608129803085, 3);
INSERT INTO `t_topic` VALUES (9602, 'admin', 1, '2019年1月14日，海关总署发布的数据显示，2018年我国民营企业进出口12.1万亿元，增长12.9%，占我国进出口总值的39.7%，比2017年提升1.1个百分点。2018年，我国民营企业对外贸进出口增长的贡献度超过50%，成为我国外贸发展的一大亮点。材料直接体现出的观点是（　　）', '[]', '[{\"A\":{\"content\":\"我国实行以公有制为主体，多种所有制经济共同发展的基本经济制度\",\"imageList\":[]}},{\"B\":{\"content\":\"民营经济已成为促进我国经济发展的重要力量\",\"imageList\":[]}},{\"C\":{\"content\":\"我国仍然处于社会主义初级阶段这一基本国情没有改变\",\"imageList\":[]}},{\"D\":{\"content\":\"我国民营经济与国有经济处于同等地位，发挥同样的作用\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '南岸区', 1608129803091, 1608129803091, 3);
