INSERT INTO `t_topic` VALUES (9611, 'admin', 1, '国家机构指国家权力机关、行政机关、司法机关等国家机关的总称。以下说法正确的是（　　）\n①全国人民代表大会是我国的最高权力机关\n②国务院和地方人民政府是我国的最高行政机关\n③最高人民法院是我国最高审判机关\n④人民检察院是我国法律监督机关', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '如皋市', 1608129803134, 1608129803134, 3);
INSERT INTO `t_topic` VALUES (9612, 'admin', 1, '全国人民代表大会和地方各级人民代表大会代表的职权有：（　　）\n①依法审议各项议案和报告\n②表决各项决定\n③提出议案和质询案\n④选举产生国家领导人', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②③④\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '昌平区', 1608129803139, 1608129803139, 3);
