INSERT INTO `t_topic` VALUES (9652, 'admin', 1, '2018年12月24日，习近平来到深圳龙华区民治街道北站社区调研时强调：要坚持依靠居民、依法有序组织居民群众参与社区治理。让居民群众依法有序参与社会治理应该（　　）', '[]', '[{\"A\":{\"content\":\"坚持基层群众自治制度，由居民群众直接行使民主权利\",\"imageList\":[]}},{\"B\":{\"content\":\"让居民群众选举产生居民委员会这一国家机关，实行自我管理\",\"imageList\":[]}},{\"C\":{\"content\":\"通过村民会议等形式，参与本村事务的决策和管理\",\"imageList\":[]}},{\"D\":{\"content\":\"实行城市居民自治，直接行使国家权力\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '顺德区', 1608129803352, 1608129803352, 3);
