INSERT INTO `t_topic` VALUES (9695, 'admin', 1, '在课堂学习过程中，大家围绕“人民法院在司法活动中，依法独立公正行使审判权”这句话展开激励的讨论，下列观点正确的是（　　）', '[]', '[{\"A\":{\"content\":\"独立行使审判权是指法院在审理过程中，主审法官个人独立行使审判权，不受任何组织和个人的影响\",\"imageList\":[]}},{\"B\":{\"content\":\"人民法院在司法活动中，只要是法院作出的判决，一律不能更改\",\"imageList\":[]}},{\"C\":{\"content\":\"政府是国家权力机关的执行机关，法院的审理应自觉接受当地政府的指示\",\"imageList\":[]}},{\"D\":{\"content\":\"依法独立公正行使审判权，是指法院在审理案件时不受行政机关、社会团体和个人的干涉\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '宁化县', 1608129803605, 1608129803605, 3);
INSERT INTO `t_topic` VALUES (9696, 'admin', 1, '坚持和完善人民代表大会制度，要求我们做到（　　）\n①必须毫不动摇坚持中国共产党的领导\n②必须保证和发展人民当家作主\n③必须全面推进以德治国\n④必须坚持民主集中制原则', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"①③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '南关区', 1608129803613, 1608129803613, 3);
INSERT INTO `t_topic` VALUES (9697, 'admin', 1, '地方各级人民政府必须依法行使行政职权。下列关于地方各级人民政府的表述正确的是（　　）\n①地方各级人民政府只对本级人民代表大会负责并报告工作\n②地方各级人民政府对本级人民代表大会和上一级国家行政机关负责并报告工作\n③地方各级人民政府是权力机关\n④地方各级人民政府根据工作需要可以设立必要的工作部门', '[]', '[{\"A\":{\"content\":\"②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②\",\"imageList\":[]}},{\"C\":{\"content\":\"①③\",\"imageList\":[]}},{\"D\":{\"content\":\"②④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '宁化县', 1608129803618, 1608129803618, 3);
INSERT INTO `t_topic` VALUES (9698, 'admin', 1, '张庆是一家私营企业的职工，去年每月有4000元的工资，此外还投资股市到年底赚了3万元，张庆所在企业的经济形式是(    )，该经济形式的地位是（　　）。', '[]', '[{\"A\":{\"content\":\"公有制经济    国民经济的主导力量\",\"imageList\":[]}},{\"B\":{\"content\":\"非公有制经济    社会主义市场经济的重要组成部分\",\"imageList\":[]}},{\"C\":{\"content\":\"混合所有制经济    社会主义经济制度的基础\",\"imageList\":[]}},{\"D\":{\"content\":\"集体经济    公有制经济的重要组成部分\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '宁化县', 1608129803623, 1608129803623, 3);
INSERT INTO `t_topic` VALUES (9699, 'admin', 1, '2018年是宁夏回族自治区成立60周年。60年来，宁夏发生了翻天覆地的变化。对此，下列说法正确的有（　　）\n①在宁夏实行民族区域自治，有力促进了民族地区经济的发展\n②宁夏回族自治区的自治机关只有宁夏市人民政府\n③设立宁夏回族自治区，体现了民族区域自治制度\n④宁夏回族自治区必须服从中央的领导', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"①③④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '南关区', 1608129803627, 1608129803627, 3);
INSERT INTO `t_topic` VALUES (9700, 'admin', 1, '如图漫画中监察委员会对公权力进行监察理解正确的是（　　）\n①能有效的强化不敢腐的震慑\n②有利于减少和遏制腐败行为的发生\n③能够公布法律、发布命令\n④对所有行使公权力的公职人员进行监察', '[\"http://static.lzxtedu.com/8b2/3309.png\"]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '南充', 1608129803632, 1608129803632, 3);
INSERT INTO `t_topic` VALUES (9701, 'admin', 1, '人民代表大会与行政机关的关系是（　　）', '[]', '[{\"A\":{\"content\":\"监督与被监督的关系\",\"imageList\":[]}},{\"B\":{\"content\":\"立法机关与行政机关的关系\",\"imageList\":[]}},{\"C\":{\"content\":\"立法机关与司法机关的关系\",\"imageList\":[]}},{\"D\":{\"content\":\"分工合作的关系\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '宁化县', 1608129803644, 1608129803644, 3);
INSERT INTO `t_topic` VALUES (9702, 'admin', 1, '行政机关是国家权力机关的执行机关，行政机关层级不同，职权也不同。在我国，最高国家行政机关是（　　）', '[]', '[{\"A\":{\"content\":\"国务院\",\"imageList\":[]}},{\"B\":{\"content\":\"青海省人民政府\",\"imageList\":[]}},{\"C\":{\"content\":\"最高人民法院\",\"imageList\":[]}},{\"D\":{\"content\":\"最高人民检察院\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '西宁', 1608129803655, 1608129803655, 3);
