INSERT INTO `t_topic` VALUES (9727, 'admin', 1, '2018年1月实施的中央一号文件指出要实施乡村振兴战略。乡村振兴需要大力发展农村集体经济。这是因为集体经济（　　）', '[]', '[{\"A\":{\"content\":\"是国民经济的主导力量\",\"imageList\":[]}},{\"B\":{\"content\":\"有利于增加公共积累、实现共同富裕\",\"imageList\":[]}},{\"C\":{\"content\":\"受国家鼓励、指导、帮助\",\"imageList\":[]}},{\"D\":{\"content\":\"是生产资料属于一部分劳动者共同所有的经济形式\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '蔚县', 1608129803788, 1608129803788, 3);
INSERT INTO `t_topic` VALUES (9728, 'admin', 1, '在农村换届选举的时候，许多村民在选举村民委员会主任时都会投上神圣的一票。对此认识错误的是（　　）', '[]', '[{\"A\":{\"content\":\"能够充分发挥人民群众的主人翁作用\",\"imageList\":[]}},{\"B\":{\"content\":\"说明村民委员会成员由村民直接选举产生\",\"imageList\":[]}},{\"C\":{\"content\":\"这是在坚持基层群众自治制度\",\"imageList\":[]}},{\"D\":{\"content\":\"村民委员会是国家权力机关\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '蔚县', 1608129803793, 1608129803793, 3);
INSERT INTO `t_topic` VALUES (9729, 'admin', 1, '我国是一个统一的多民族国家。维护、巩固民族团结，要坚持（　　）\n①民族区域自治制度\n②平等团结互助和谐的社会主义民族关系\n③设立自治机关，行使自治权\n④把国家的法律、政策和民族自治地方的具体实际、特殊情况结合起来', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②③④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '蔚县', 1608129803799, 1608129803799, 3);
