INSERT INTO `t_topic` VALUES (9769, 'admin', 1, '在学习宪法中的“国家机构”这一内容时，张强上网查到了下列部门或机关，其中哪一组属于“国家机关”呢？（　　）\n①晋城市人民政府\n②清徐县监察委员会\n③太原市迎泽区工会\n④晋城市政协\n⑤祁县某乡党委\n⑥太原钢铁集团有限公司\n⑦晋城市人大\n⑧临汾市尧都区人民检察院\n⑨运城市平陆县某村村委会', '[]', '[{\"A\":{\"content\":\"①④⑧⑨\",\"imageList\":[]}},{\"B\":{\"content\":\"①②⑦⑧\",\"imageList\":[]}},{\"C\":{\"content\":\"③④⑤⑨\",\"imageList\":[]}},{\"D\":{\"content\":\"①②⑤⑧\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '高平市', 1608129804010, 1608129804010, 3);
INSERT INTO `t_topic` VALUES (9770, 'admin', 1, '公有制经济和非公有制经济都是社会主义市场经济的重要组成部分。八年级学生小李看了这句话想，现实生活中哪些企业是公有制呢？请你帮他选出都属于公有制经济的一组（　　）\n①中国水利\n②肯德基\n③百度\n④美特好超市\n⑤中国银行\n⑥中国移动', '[]', '[{\"A\":{\"content\":\"②③④\",\"imageList\":[]}},{\"B\":{\"content\":\"①④⑤\",\"imageList\":[]}},{\"C\":{\"content\":\"①⑤⑥\",\"imageList\":[]}},{\"D\":{\"content\":\"②④⑥\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '高平市', 1608129804015, 1608129804015, 3);
INSERT INTO `t_topic` VALUES (9771, 'admin', 1, '2018年11月1日，习近平总书记在民营企业座谈会上指出，改革开放40年来，我国民营经济已经成为推动我国发展不可或缺的力量……我国民营经济只能壮大，不能弱化，不仅不能“离场”，而且要走向更加广阔的舞台。我国对待民营经济的态度是（　　）', '[]', '[{\"A\":{\"content\":\"巩固和发展\",\"imageList\":[]}},{\"B\":{\"content\":\"鼓励、支持、引导\",\"imageList\":[]}},{\"C\":{\"content\":\"鼓励、指导和帮助\",\"imageList\":[]}},{\"D\":{\"content\":\"把民营经济作为市场经济的主导力量\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '遵义', 1608129804020, 1608129804020, 3);
INSERT INTO `t_topic` VALUES (9772, 'admin', 1, '下列选项中不能体现民族平等、民族团结、各民族共同繁荣的是（　　）', '[]', '[{\"A\":{\"content\":\"让少数民族地区充分享有高度自治权\",\"imageList\":[]}},{\"B\":{\"content\":\"推动西部大开发战略，实施兴边富民行动\",\"imageList\":[]}},{\"C\":{\"content\":\"国家支持民族地区发展教育，实施积极的就业政策\",\"imageList\":[]}},{\"D\":{\"content\":\"大力扶持少数民族文化的保护、继承、创新和发展工作\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '遵义', 1608129804025, 1608129804025, 3);
INSERT INTO `t_topic` VALUES (9773, 'admin', 1, '每年3月，我国都要召开全国人民代表大会，商讨决策国家大事，会议期间要审议国务院、最高人民法院、最高人民检察院的工作报告，审议通过了一些国家的重要法律、发展纲要等。这表明（　　）\n①人民代表大会是我国的根本政治制度\n②全国人民代表大会是最高国家权力机关\n③全国人民代表大会有对重大事项的决定权和监督权\n④只有全国人民代表大会才是人民行使当家作主权力的机关', '[]', '[{\"A\":{\"content\":\"①②\",\"imageList\":[]}},{\"B\":{\"content\":\"②③\",\"imageList\":[]}},{\"C\":{\"content\":\"①③\",\"imageList\":[]}},{\"D\":{\"content\":\"②④\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '崇川区', 1608129804030, 1608129804030, 3);
INSERT INTO `t_topic` VALUES (9774, 'admin', 1, '观察漫画《比翼齐飞》，对其寓意理解不正确的是（　　）', '[\"http://static.lzxtedu.com/8b2/3387.png\"]', '[{\"A\":{\"content\":\"这是由我国生产力水平还不高、发展还不平衡决定的\",\"imageList\":[]}},{\"B\":{\"content\":\"我国毫不动摇地鼓励、支持和引导非公有制经济的发展\",\"imageList\":[]}},{\"C\":{\"content\":\"国有经济和民营经济在国民经济中具有相同的地位和作用\",\"imageList\":[]}},{\"D\":{\"content\":\"我国坚持公有制为主体、多种所有制经济共同发展的基本经济制度\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '崇川区', 1608129804036, 1608129804036, 3);
INSERT INTO `t_topic` VALUES (9775, 'admin', 1, '小华有将教材中重要知识点摘抄在笔记本上的习惯。但由于他的粗心，以下有句话出现了错误，这句话是（　　）', '[]', '[{\"A\":{\"content\":\"实行基层群众自治制度，发展基层民主，是社会主义民主政治建设的基础\",\"imageList\":[]}},{\"B\":{\"content\":\"中国共产党领导的多党合作和政治协商制度强调通过充分协商，求同存异，找到最大公约数，画出最大同心圆\",\"imageList\":[]}},{\"C\":{\"content\":\"民族区域自治制度是一项独具中国特色的实现民族平等、保障少数民族合法权利的基本政治制度\",\"imageList\":[]}},{\"D\":{\"content\":\"人民代表大会制度是我国的根本制度，是人民掌握国家政权、行使权利的根本途径\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '重庆', 1608129804041, 1608129804041, 3);
INSERT INTO `t_topic` VALUES (9776, 'admin', 1, '近日，一部讲述改革开放大背景下，先行者们在变革浪潮中不断探索和突围的电视剧《大江大河》在各大电视台热播。剧中，小雷家村在支书雷东宝的带领和政府支持下创建村办砖厂、养猪场、电线厂，带动了村民的就业，增加了村民收入，实现了从一个落后贫穷的小山村向富余殷实的全省先进模范村的转变。这体现了（　　）', '[]', '[{\"A\":{\"content\":\"国有经济是社会主义经济制度的基础\",\"imageList\":[]}},{\"B\":{\"content\":\"村办企业有利于支撑经济增长、增加税收\",\"imageList\":[]}},{\"C\":{\"content\":\"国家鼓励、支持、引导非公有制经济的发展\",\"imageList\":[]}},{\"D\":{\"content\":\"集体经济对于扩大就业、增加公共积累具有重要作用\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '渝中区', 1608129804046, 1608129804046, 3);
INSERT INTO `t_topic` VALUES (9777, 'admin', 1, '迪迪是某校八年级的一位政治老师，他任教的班级刚结束了《人民当家作主》的学习。某天他围绕此单元对以下四位同学进行抽查，其中上课最认真的是（　　）', '[]', '[{\"A\":{\"content\":\"心心：十三届全国人大常委会第六次会议表决通过关于修改刑事诉讼法的决定--全国人大行使决定权\",\"imageList\":[]}},{\"B\":{\"content\":\"涵涵：重庆市人民政府向重庆市人大报告工作--权力机关对同级行政机关实施监督\",\"imageList\":[]}},{\"C\":{\"content\":\"辰辰：人民代表大会审议财政决算报告--宪法规定社会主义经济制度明确人民行使权力的基本途径\",\"imageList\":[]}},{\"D\":{\"content\":\"劲劲：人民政协对国家大政方针提出批评--人民政协行使民主决策的职能\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '渝中区', 1608129804051, 1608129804051, 3);
INSERT INTO `t_topic` VALUES (9778, 'admin', 1, '下列属于我国基本政治制度的是（　　）', '[]', '[{\"A\":{\"content\":\"计划生育\",\"imageList\":[]}},{\"B\":{\"content\":\"保护环境\",\"imageList\":[]}},{\"C\":{\"content\":\"民族区域自治制度\",\"imageList\":[]}},{\"D\":{\"content\":\"对外开放\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '龙凤区', 1608129804055, 1608129804055, 3);
INSERT INTO `t_topic` VALUES (9779, 'admin', 1, '发展社会主义民主，把人民当家作主落实到国家政治生活和社会生活之中。下列属于“发展社会主义民主，切实保障人民当家作主的民主权利”的制度保障的有（　　）\n①中国共产党领导的多党合作和政治协商制度\n②实行基层群众自治制度\n③坚持和完善人民代表大会制度\n④坚持和完善民族区域自治制度', '[]', '[{\"A\":{\"content\":\"①③④\",\"imageList\":[]}},{\"B\":{\"content\":\"②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②③④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '雨城区', 1608129804061, 1608129804061, 3);
INSERT INTO `t_topic` VALUES (9780, 'admin', 1, '“选举民主”和“协商民主”是我国社会主义民主的两种重要形式。下列关于社会主义协商民主说法正确的有（　　）\n①发展协商民主要推动人民的事情人民商量\n②它是我国的根本政治制度\n③协商民主是我国社会主义民主政治的特有形式和独特优势\n④它是公民履行参政议政职能、直接参与国家治理的重要方式', '[]', '[{\"A\":{\"content\":\"①③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②\",\"imageList\":[]}},{\"C\":{\"content\":\"②④\",\"imageList\":[]}},{\"D\":{\"content\":\"③④\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '黄石', 1608129804066, 1608129804066, 3);
INSERT INTO `t_topic` VALUES (9781, 'admin', 1, '李克强总理在2018年《政府工作报告》中指出：打造共建共治共享社会治理格局。完善基层群众自治制度，加强社区治理。在我国，基层群众自治制度有（　　）\n①人民代表大会制度\n②农村村民自治制度\n③民族区域自治制度\n④城市居民自治制度', '[]', '[{\"A\":{\"content\":\"①③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②\",\"imageList\":[]}},{\"C\":{\"content\":\"②④\",\"imageList\":[]}},{\"D\":{\"content\":\"③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '惠城区', 1608129804071, 1608129804071, 3);
INSERT INTO `t_topic` VALUES (9782, 'admin', 1, '2018年3月5日，第十三届全国人民代表大会第一次会议在北京召开这表明，我国的根本政治制度是（　　）', '[]', '[{\"A\":{\"content\":\"人民代表大会\",\"imageList\":[]}},{\"B\":{\"content\":\"政治协商制度\",\"imageList\":[]}},{\"C\":{\"content\":\"人民代表大会制度\",\"imageList\":[]}},{\"D\":{\"content\":\"民族区城自治制度\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '定安县', 1608129804076, 1608129804076, 3);
INSERT INTO `t_topic` VALUES (9783, 'admin', 1, '小明的叔叔经营着一家煤炭转化加工的小企业，受环保督查的影响被关停。一次聊天中，小明想用所学知识劝劝叔叔，你觉得哪一组合适呢？（　　）\n①毫不动摇巩固和发展公有制经济\n②毫不动摇鼓励、支持、引导非公有制经济发展\n③中国经济发展进入新常态，更多关注经济增长速度\n④以牺牲环境为代价，不论是国有企业还是民营企业，都应当退出历史舞台\n⑤遵纪守法办企业，光明正大搞经营，创新发展新动力，健康成长大空间', '[\"http://static.lzxtedu.com/8b2/3399.png\"]', '[{\"A\":{\"content\":\"①③④\",\"imageList\":[]}},{\"B\":{\"content\":\"②④⑤\",\"imageList\":[]}},{\"C\":{\"content\":\"②③⑤\",\"imageList\":[]}},{\"D\":{\"content\":\"①②④⑤\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '阳城县', 1608129804080, 1608129804080, 3);
INSERT INTO `t_topic` VALUES (9784, 'admin', 1, '小杨同学学习了基本经济制度的有关知识后，搜集了本地的材料加深理解，他的阐述中，哪个不正确？（　　）', '[]', '[{\"A\":{\"content\":\"私营经济--蟒河镇邢西、押水、蟒河三个行政村在阳城县农林文旅康先行先试示范区内，进行资源变资产、资金变股金、农民变股东“三变”改革\",\"imageList\":[]}},{\"B\":{\"content\":\"外资经济--阳城国际发电有限责任公司由中美两国、晋苏两省共五方投资组建\",\"imageList\":[]}},{\"C\":{\"content\":\"国有经济--大唐阳城发电有限责任公司是由中国大唐集团公司（由中央直接管理的国有独资公司）、江苏省国信资产管理集团有限公司、山西国际电力集团有限公司分别以41%、30%、29%比例共同投资组建的发电企业\",\"imageList\":[]}},{\"D\":{\"content\":\"个体经济--全国道德模范候选人，东关村栗红连老人，二十多年来，行程7万多公里，累计为村民和孤寡老人义务理发7万多次，经营着一家小小的理发店\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '阳城县', 1608129804085, 1608129804085, 3);
INSERT INTO `t_topic` VALUES (9785, 'admin', 1, '2017年11月7日，新华社北京《中共19大文件汇编》的蒙古、维吾尔、哈萨克，朝鲜等五种少数民族文字单行本由民族出版社出版并向全国发行。这表明（　　）', '[]', '[{\"A\":{\"content\":\"我国各族在文化上享有平等的权利\",\"imageList\":[]}},{\"B\":{\"content\":\"我国实行民族区域自治制度\",\"imageList\":[]}},{\"C\":{\"content\":\"我国各民族已真正成为国家的主人\",\"imageList\":[]}},{\"D\":{\"content\":\"尊重少数民族的语言文字，是公民的义务\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '香洲区', 1608129804090, 1608129804090, 3);
INSERT INTO `t_topic` VALUES (9786, 'admin', 1, '2018年3月11日第十三届全国人民代表大会第一次会议通过宪法修正案，其中宪法第六十七条“全国人民代表大会常务委员会行使下列职权”中第六项修改为（六）监督国务院、中央军事委员会、国家监察委员会最高人民法院和最高人检察院的工作”。这表明（　　）', '[]', '[{\"A\":{\"content\":\"通过法治保障的民主才是人民自由幸福的前提\",\"imageList\":[]}},{\"B\":{\"content\":\"人民代表大会制度是我国的根本政治制度\",\"imageList\":[]}},{\"C\":{\"content\":\"发展基层民主是社会主义民主建设的基础\",\"imageList\":[]}},{\"D\":{\"content\":\"社会主义民主开始在中国大地上得到真正确立\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '莒县', 1608129804094, 1608129804094, 3);
INSERT INTO `t_topic` VALUES (9787, 'admin', 1, '截至2019年6月，参与“万企帮万村”精准扶贫行动的民营企业达8.81万家，精准帮扶10.27万个村，产业投入753.71亿元，公益投入139.1亿元，安置就业66.15万人，技能培训94.1万人，共带动和惠及1163万建档立卡贫困人口。下列对材料中民营企业行为评述正确的有（　　）\n①有助于我国早日打赢脱贫攻坚战，帮助贫困群众过上好日子\n②这是党和政府对民营企业的要求，是其社会责任\n③体现了民营企业积极承担社会责任，为社会发展服务\n④体现了党和政府坚持以人民为中心的发展思想', '[]', '[{\"A\":{\"content\":\"①②\",\"imageList\":[]}},{\"B\":{\"content\":\"③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③\",\"imageList\":[]}},{\"D\":{\"content\":\"②④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '金牛区', 1608129804099, 1608129804099, 3);
INSERT INTO `t_topic` VALUES (9788, 'admin', 1, '党的十九大报告指出：“要支持民营企业发展，激发各类市场主体活力，要努力实现更高质量、更有效率、更加公平、更可持续的发展”。民营企业用近40%的资源，创造了我国60%以上GDP，缴纳了50%以上的税收，贡献了70%以上的技术创新和新产品开发，提供了80%以上的就业岗位。这说明（　　）', '[]', '[{\"A\":{\"content\":\"要毫不动摇的坚持非公有制经济的主体地位不动摇\",\"imageList\":[]}},{\"B\":{\"content\":\"非公有制经济是社会主义公有制经济的重要组成部分\",\"imageList\":[]}},{\"C\":{\"content\":\"公有制经济的主体地位已经发生改变\",\"imageList\":[]}},{\"D\":{\"content\":\"非公有制经济对增强经济活力、带动就业等具有重要意义\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '官渡区', 1608129804106, 1608129804106, 3);
INSERT INTO `t_topic` VALUES (9789, 'admin', 1, '人民掌握国家政权，行使权力的根本途径是（　　）', '[]', '[{\"A\":{\"content\":\"人民代表大会制度\",\"imageList\":[]}},{\"B\":{\"content\":\"基层群众自治制度\",\"imageList\":[]}},{\"C\":{\"content\":\"多党合作与政治协商制度\",\"imageList\":[]}},{\"D\":{\"content\":\"人民代表大会\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '忻城县', 1608129804110, 1608129804110, 3);
INSERT INTO `t_topic` VALUES (9790, 'admin', 1, '党的十九大报告指出：发展社会主义民主政治就是要体现人民意志、保障人民权益、激发人民创造活力，用制度体系保证人民当家作主。下列对保证人民当家作主“制度体系”认识正确的是（　　）', '[]', '[{\"A\":{\"content\":\"中国共产党领导的多党合作和政治协商制度是人民行使权力的根本途径\",\"imageList\":[]}},{\"B\":{\"content\":\"人民代表大会制度是保障少数民族合法权利的基本制度\",\"imageList\":[]}},{\"C\":{\"content\":\"民族区域自治制度通过充分协商，凝聚共识\",\"imageList\":[]}},{\"D\":{\"content\":\"实行基层群众自治制度，发展基层民主，是社会主义民主政治建设的基础\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '邵武市', 1608129804115, 1608129804115, 3);
INSERT INTO `t_topic` VALUES (9791, 'admin', 1, '某市推行村民“说事会”，村委会议定的事项全部公示并接受群众监督，定期召开村民说事会议。行村民“说事会”的做法体现了（　　）\n①群众参与“说事”是我国公民行使民主权利的主要途径\n②该村民“说事会”是我国国家权力机关的一个重要组成部分\n③群众参与“说事”是社会主义民主政治本质特征的表现\n④召开村民说事会议，符合完善我国基本政治制度的要求', '[]', '[{\"A\":{\"content\":\"①④\",\"imageList\":[]}},{\"B\":{\"content\":\"②③\",\"imageList\":[]}},{\"C\":{\"content\":\"③⑤\",\"imageList\":[]}},{\"D\":{\"content\":\"③④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '宁远县', 1608129804120, 1608129804120, 3);
INSERT INTO `t_topic` VALUES (9792, 'admin', 1, '观察如图漫画，国家之所以这样做，是因为（　　）\n①国家鼓励、支持、引导非公有制经济的发展\n②国家毫不动摇地巩固和发展公有制经济\n③非公有制经济是社会主义市场经济的重要组成部分\n④非公有制经济对促进生产力发展起着主导作用', '[\"http://static.lzxtedu.com/8b2/3410png\"]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"①③④\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '兰陵县', 1608129804124, 1608129804124, 3);
INSERT INTO `t_topic` VALUES (9793, 'admin', 1, '改革开放以来，我国的非公有制经济创造了60%左右的国内生产总值，80%左右的社会就业，提供了超过一半的税收。这表明（　　）', '[]', '[{\"A\":{\"content\":\"我国实行的是非公有制经济为主体的基本经济制度\",\"imageList\":[]}},{\"B\":{\"content\":\"改革就是让非公有制经济在资源配置中起决定作用\",\"imageList\":[]}},{\"C\":{\"content\":\"我国允许非公有制经济和公有制经济共同发展\",\"imageList\":[]}},{\"D\":{\"content\":\"在我国公有制经济和非公有制经济地位是平等的\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '巴林左旗', 1608129804129, 1608129804129, 3);
