INSERT INTO `t_topic` VALUES (9795, 'admin', 1, '民族区域自治制度是我国解决民族问题的基本制度。下列关于民族区域自治的说法，正确的有（　　）\n①民族区域自治是国家统一领导与少数民族区域自治的结合\n②民族自治地方分为自治区、自治市、自治县、民族乡\n③各少数民族群居的地方实行区域自治\n④民族自治地方的自治机关实行民主集中制的原则', '[]', '[{\"A\":{\"content\":\"①②④\",\"imageList\":[]}},{\"B\":{\"content\":\"①④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②③\",\"imageList\":[]}},{\"D\":{\"content\":\"①②③④\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '耒阳市', 1608129804142, 1608129804142, 3);
