INSERT INTO `t_topic` VALUES (9807, 'admin', 1, '近年来，中华人民共和国最高人民检察院对受理申诉或办案中发现的18起重大冤假错案，及时提出抗诉或再审检查建议，人民法院均改判无罪。司法机关纠正冤假错案体现了（　　）', '[]', '[{\"A\":{\"content\":\"依法行政，切实维护人民的合法利益\",\"imageList\":[]}},{\"B\":{\"content\":\"科学立法，使立法符合科学性、正义性\",\"imageList\":[]}},{\"C\":{\"content\":\"严格执法，做到违法必究、执法必严\",\"imageList\":[]}},{\"D\":{\"content\":\"公正司法，努力在司法审查过程中维护公平正义\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '海陵区', 1608129804228, 1608129804228, 3);
