INSERT INTO `t_topic` VALUES (9820, 'admin', 1, '2019年是西藏民主改革60周年。西藏民主改革以来，自治区的自治机关在经济、政治、文化等方面享有一定的自治权。在自治区人大代表中，藏族和其他少数民族代表超过90%。由此可见（　　）', '[]', '[{\"A\":{\"content\":\"我国是一个统一的多民族国家\",\"imageList\":[]}},{\"B\":{\"content\":\"西藏自治区民族自治机关必须服从中央的领导\",\"imageList\":[]}},{\"C\":{\"content\":\"西藏自治区实行高度自治\",\"imageList\":[]}},{\"D\":{\"content\":\"民族区域自治制度维护了少数民族的自主平等\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '广陵区', 1608129804294, 1608129804294, 3);
