INSERT INTO `t_topic` VALUES (9838, 'admin', 1, '我校九年级同学们为了更好地理解“人大代表的职责”，走出课堂开展了“我与人大代表零距离”交流活动。活动中特别邀请了渝中区人大代表--我校王国华校长与同学们见面交流。下列是互动交流环节中部分学生的发言，你认为不正确的是（　　）', '[]', '[{\"A\":{\"content\":\"谢谢您每年在我国国家权力机关中反映群众的心声\",\"imageList\":[]}},{\"B\":{\"content\":\"谢谢你时常联系人民群众，听取和反映大家的意见和要求\",\"imageList\":[]}},{\"C\":{\"content\":\"我了解到你们每年都会审议各项议案和报告、表决各项决定\",\"imageList\":[]}},{\"D\":{\"content\":\"我知道您可以代表人民行使立法权、决定权、任免权和监督权\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '渝中区', 1608129804390, 1608129804390, 3);
