INSERT INTO `t_topic` VALUES (1438886804991299587, 'admin', 1, '12月3日是第28个国际残疾人日，来自东莞的蔡勇斌获得广东省自强模范称号，他六岁时双目失明。他开发出了大大小小百余款盲人辅助软件，从此改变了盲人的上网方式。他说“虽然我看不见，但是我觉得我不能去浪费这仅有一次的生命”这体现了（　　）', '[]', '[{\"A\":{\"content\":\"正确认识自己，有助于我们增强信心，更好地发展自己的潜能\",\"imageList\":[]}},{\"B\":{\"content\":\"他这是通过他人评价自己\",\"imageList\":[]}},{\"C\":{\"content\":\"他这是通过在群体中的关系来认识自己\",\"imageList\":[]}},{\"D\":{\"content\":\"正确认识自己能理解、宽容和善待他人，与他们积极互动\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '东莞市', 1631892322195, 1631892322195, 1);
