INSERT INTO `t_topic` VALUES (1438886805054214146, 'admin', 1, '古人云：“役其所长，则事无废功；避其所短，则世无弃材矣，”这句话说明，做更好的自己（　　）', '[]', '[{\"A\":{\"content\":\"就要扬长避短\",\"imageList\":[]}},{\"B\":{\"content\":\"就要主动改正缺点\",\"imageList\":[]}},{\"C\":{\"content\":\"需要不断激发自己的潜能\",\"imageList\":[]}},{\"D\":{\"content\":\"需要和他人一起成长\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '铁锋区', 1631892322215, 1631892322215, 1);
