INSERT INTO `t_topic` VALUES (1438886805117128707, 'admin', 1, '梦想变为现实需要努力。因为努力（　　）\n①就一定会取得成功\n②是一种生活态度\n③是对自我的坚定信念和对美好的不懈追求\n④是一种不服输的坚韧和失败后从头再来的勇气', '[]', '[{\"A\":{\"content\":\"①②\",\"imageList\":[]}},{\"B\":{\"content\":\"②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②③④\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '天峨县', 1631892322234, 1631892322234, 1);
