INSERT INTO `t_topic` VALUES (1438886806731935747, 'admin', 1, '“风声、雨声、读书声，声声入耳；家事、国事、天下事，事事关心。”这幅对联启示我们在学习中要做到（　　）', '[]', '[{\"A\":{\"content\":\"行己有耻\",\"imageList\":[]}},{\"B\":{\"content\":\"知行合一\",\"imageList\":[]}},{\"C\":{\"content\":\"学贵有疑\",\"imageList\":[]}},{\"D\":{\"content\":\"止于至善\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '丹阳市', 1631892322614, 1631892322614, 1);
INSERT INTO `t_topic` VALUES (1438886806731935748, 'admin', 1, '“不积跬步，无以至千里，不积小流，无以成江海。”这句古语给我们的启示是（　　）', '[]', '[{\"A\":{\"content\":\"只要行动就能实现梦想\",\"imageList\":[]}},{\"B\":{\"content\":\"积累是实现梦想的前提\",\"imageList\":[]}},{\"C\":{\"content\":\"努力要从点滴做起，每天进步一点点才有可能实现梦想\",\"imageList\":[]}},{\"D\":{\"content\":\"我们是为父母而学，不是为自己，要让学习没有压力\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '昭阳区', 1631892322619, 1631892322619, 1);
INSERT INTO `t_topic` VALUES (1438886806794850305, 'admin', 1, '“诗和远方”是人们的美好祝愿，美好期待，而要实现“诗意”抵达，却需要风雨兼程。这段话给我们的启示是（　　）', '[]', '[{\"A\":{\"content\":\"实现理想，需要脚踏实地地、全力以赴\",\"imageList\":[]}},{\"B\":{\"content\":\"实现理想，只需对人生作出长远规划\",\"imageList\":[]}},{\"C\":{\"content\":\"只要理想是自己规划的就一定能实现\",\"imageList\":[]}},{\"D\":{\"content\":\"少年的理想与个人人生目标紧密相连\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '淅川县', 1631892322625, 1631892322625, 1);
INSERT INTO `t_topic` VALUES (1438886806794850306, 'admin', 1, '为了给孩子在教室中占据“好”位子，家长使出浑身解数……2019年春晚小品《占位子》引发大家热议。其实，对学生来说，取得良好的学习效果，重要的是（　　）\n①把所有时间都用来刷题\n②激发学习兴趣，找到万能而有效的学习方法\n③上课专心，注意力集中\n④勤学好问、认真思考、主动学习\n', '[]', '[{\"A\":{\"content\":\"②③\",\"imageList\":[]}},{\"B\":{\"content\":\"②④\",\"imageList\":[]}},{\"C\":{\"content\":\"③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '正定县', 1631892322630, 1631892322630, 1);
INSERT INTO `t_topic` VALUES (1438886806861959170, 'admin', 1, '中国工程院院士罗锡文说：“小学印象模糊，大学印象不深刻，唯独中学在我的人生中留下抹不去的记忆。我后来能做点事情，与在中学时代打下的基础密不可分。”下列对我们中学时代的认识正确的是（　　）\n①决定了我们一生的命运\n②开始主动发现和认识自己\n③见证着从少年到青年的生命进阶\n④开始自觉磨砺意志和品格，思考生活的意义', '[]', '[{\"A\":{\"content\":\"①②③④\",\"imageList\":[]}},{\"B\":{\"content\":\"①③④\",\"imageList\":[]}},{\"C\":{\"content\":\"②③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②③\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '正定县', 1631892322635, 1631892322635, 1);
INSERT INTO `t_topic` VALUES (1438886806861959171, 'admin', 1, '歌曲《向梦想出发》唱到：“为梦想而生，带着梦想出发。心怀天下，永不停歇，我们使命必达。”下列对此理解正确的是（　　）\n①有梦想就一定能成功\n②个人梦和中国梦密不可分\n③梦想能激发生命的热情和勇气\n④梦想与个人的人生目标紧密相连', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '巴南区', 1631892322641, 1631892322641, 1);
INSERT INTO `t_topic` VALUES (1438886806861959172, 'admin', 1, '科技日新月异，信息化、5G、人工智能正以排山倒海之势席卷而至，为了更好地适应未来社会的发展，我们必须树立终身学习的观念。下列体现这一思想的是（　　）', '[]', '[{\"A\":{\"content\":\"少壮不努力，老大徙伤悲\",\"imageList\":[]}},{\"B\":{\"content\":\"读书破万卷，下笔如有神\",\"imageList\":[]}},{\"C\":{\"content\":\"吾生也有涯，而知也无涯\",\"imageList\":[]}},{\"D\":{\"content\":\"人学始知道，不学非自然\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '临颍县', 1631892322647, 1631892322647, 1);
INSERT INTO `t_topic` VALUES (1438886806924873729, 'admin', 1, '周恩来在中学时代立下了“为中华之崛起而读书”的志向，长大后他成为了我国伟大的 无产阶级革命家；瓦特在中学时代注重观察周边事物，善于思考，后来他成功改良了蒸汽机；钱学森说中学学习生活对他的知识和人生观起了很大作用。这告诉我们（　　）', '[]', '[{\"A\":{\"content\":\"只要读了中学就能成才\",\"imageList\":[]}},{\"B\":{\"content\":\"中学时代比小学时代重要\",\"imageList\":[]}},{\"C\":{\"content\":\"中学时代为人的一生奠定重要基础\",\"imageList\":[]}},{\"D\":{\"content\":\"中学时代见证着一个人从少年到青年的生命进阶\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '巴南区', 1631892322653, 1631892322653, 1);
INSERT INTO `t_topic` VALUES (1438886806924873730, 'admin', 1, '古人云：“役其所长，则事无废功；避其所短，则世无弃材矣。”这句话启示我们，做更好的自己，就要（　　）', '[]', '[{\"A\":{\"content\":\"扬长避短\",\"imageList\":[]}},{\"B\":{\"content\":\"与他人合作\",\"imageList\":[]}},{\"C\":{\"content\":\"树立远大理想\",\"imageList\":[]}},{\"D\":{\"content\":\"欣赏自己\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '五峰县', 1631892322659, 1631892322659, 1);
INSERT INTO `t_topic` VALUES (1438886806924873731, 'admin', 1, '为了给孩子在教室中占据“好”位子，家长使出浑身解数……2019年春晚小品《占位子》引发大家热议。其实，对学生来说，要取得良好的学习效果必须（　　）\n①学会自主、合作、探究式学习\n②上课专心，有难题要及时“借鉴”同学作业\n③勤学好问、认真思考、主动学习\n④激发学习兴趣，找到万能而有效的考试技巧', '[]', '[{\"A\":{\"content\":\"①②\",\"imageList\":[]}},{\"B\":{\"content\":\"①③\",\"imageList\":[]}},{\"C\":{\"content\":\"②④\",\"imageList\":[]}},{\"D\":{\"content\":\"③④\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '南平', 1631892322665, 1631892322665, 1);
INSERT INTO `t_topic` VALUES (1438886806991982594, 'admin', 1, '观察下面的系列图片，你能从中获取的信息是（　　）\n①人的成长就是一个不断学习的过程，学习能够让我们拥有更充实的生活\n②学习与生命发展同步，学习点亮我们心中的明灯，激发前进的动力\n③学习有苦也有乐，我们都需要面对\n④树立终身学习的观念，学习没有终点', '[\"http://static.lzxtedu.com/7a2/1225.png\"]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①③④\",\"imageList\":[]}},{\"C\":{\"content\":\"②③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '襄汾县', 1631892322672, 1631892322672, 1);
INSERT INTO `t_topic` VALUES (1438886806991982595, 'admin', 1, '一些学校除了教室卫生由学生自己打扫外，其余的几乎全部由保洁公司来做，对此，请你发表看法。如果从学生的角度来说，合适的理由是（　　）\n①学校的出发点是好的，是为了学生的安全，也为了节省学生更多时间去学习\n②这种做法会使学生形成懒惰的行为，不利养成劳动的习惯\n③可以便于管理，避免好多安全隐患，可以更高质量的保持学校的整洁风貌\n④影响学生的自理能力和自强品质的培养\n', '[]', '[{\"A\":{\"content\":\"①③\",\"imageList\":[]}},{\"B\":{\"content\":\"②④\",\"imageList\":[]}},{\"C\":{\"content\":\"①④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '襄汾县', 1631892322678, 1631892322678, 1);
