INSERT INTO `t_topic` VALUES (1438886812008370178, 'admin', 1, '为了给班级出黑板报，融融和同学们忙到很晚，耽误了写作业时间，匆忙中抄袭了同学的作业，老师知道后，严肃的批评了他。妈妈认为他应以学习为重，尽量不参加集体活动，对此融融应该（　　）\n①正确对待学习，先保证完成学习任务\n②对自己的学习负责，养成诚信的品质\n③主动沟通，争取得到老师的支持\n④正确对待老师和父母的教育，合理安排班级工作', '[]', '[{\"A\":{\"content\":\"②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③\",\"imageList\":[]}},{\"D\":{\"content\":\"②④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '福清市', 1631892323869, 1631892323869, 1);
