INSERT INTO `t_topic` VALUES (1438886812075479042, 'admin', 1, '面对“学习”，有的同学兴趣盎然，成果斐然；有的同学枯燥厌学，不思进取。为什么会这样呢？从根本上讲是对“学习”的意义认识不足。以下是部分同学对“为什么学习”的认识，你同意哪些观点呢（　　）\n小朋：我爷爷说“书中自有黄金屋，书中自有颜如玉”\n小刘：学习能让我们更好地生存，拥有更充实的生命\n小倩：上个好高中、好大学，找个好工作，挣好多钱\n小菲：学习能使我们掌握更多本领，在服务社会中实现人生价值', '[]', '[{\"A\":{\"content\":\"小刘、小菲\",\"imageList\":[]}},{\"B\":{\"content\":\"小刘、小倩\",\"imageList\":[]}},{\"C\":{\"content\":\"小倩、小菲\",\"imageList\":[]}},{\"D\":{\"content\":\"小朋、小刘\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '栾城区', 1631892323887, 1631892323887, 1);
