INSERT INTO `t_topic` VALUES (1438886812205502466, 'admin', 1, '“一天不读书，根本看不出；一周不读书，出口就爆粗；一月不读书，智商输给猪”；“即使现在，你的对手还在不停地翻动书页”。这两句话体现的学习观有（　　）\n①学习是人类的基本活动\n②学习能使自己得到发展\n③要确立终身学习的观念\n④过重的学习压力才能赢\n', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '醴陵市', 1631892323915, 1631892323915, 1);
