INSERT INTO `t_topic` VALUES (1438886814034219010, 'admin', 1, '某市初中学业水平考试中，语文学科的命题作文是“我不一般”。这个作文的主要立意点是让学生学会（　　）', '[]', '[{\"A\":{\"content\":\"发现他人的优点\",\"imageList\":[]}},{\"B\":{\"content\":\"接纳与欣赏自己\",\"imageList\":[]}},{\"C\":{\"content\":\"赞美他人\",\"imageList\":[]}},{\"D\":{\"content\":\"获得快乐的方法\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '盐田区', 1631892324346, 1631892324346, 1);
INSERT INTO `t_topic` VALUES (1438886814034219011, 'admin', 1, '小虎第一次走进中学校园时，感到兴奋、自豪。面对这么多陌生的新同学，他心里很不平静，心想：“我能和他们友好相处吗？我能适应中学生活吗？”小虎之所以这样想，是因为中学生活对我们来说意味着（　　）\n①一个别样的天地，站在一个新的起点上\n②没有什么变化，只是从六年级变成七年级而已\n③我们已长大成人了，可以想干什么就干什么\n④新的机会和可能、新的目标和挑战', '[]', '[{\"A\":{\"content\":\"①②\",\"imageList\":[]}},{\"B\":{\"content\":\"①④\",\"imageList\":[]}},{\"C\":{\"content\":\"②③\",\"imageList\":[]}},{\"D\":{\"content\":\"③④\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '盐田区', 1631892324352, 1631892324352, 1);
