INSERT INTO `t_topic` VALUES (1438886822208917507, 'admin', 1, '2019年11月19日，最高人民法院发布《司法大数据专题报告之网络犯罪特点和趋势》。报告显示，2018年，微信超过QQ成为网络诈骗犯罪中使用最为频繁的犯罪工具，超半数网络诈骗案件中均有涉及应用微信实施诈骗的犯罪情节。这给我们的警示是（　　）', '[]', '[{\"A\":{\"content\":\"要拒绝使用微信，不能通过微信交友\",\"imageList\":[]}},{\"B\":{\"content\":\"在微信上要提高警惕，要学会保护自己\",\"imageList\":[]}},{\"C\":{\"content\":\"微信开启了通往世界的一个窗口\",\"imageList\":[]}},{\"D\":{\"content\":\"微信能够把不认识的人连在一起\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '莲湖区', 1631892326300, 1631892326300, 2);
