INSERT INTO `t_topic` VALUES (1438886822406049793, 'admin', 1, '网络社交已经成为中学生交友的新方式。有的人通过网络聊天开拓了视野，增进了友谊，也有不少人上当受骗，在人生中留下惨痛教训。因此，在网络交往中我们应该（　　）', '[]', '[{\"A\":{\"content\":\"提供个人虚假信息，以免个人隐私被泄露\",\"imageList\":[]}},{\"B\":{\"content\":\"既要谨慎、真诚、守信，又要区分是非善恶\",\"imageList\":[]}},{\"C\":{\"content\":\"对长期交往的网友要提供个人真实信息\",\"imageList\":[]}},{\"D\":{\"content\":\"依赖网络广交朋友，并接受网友邀请随时约见网友\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '东台市', 1631892326344, 1631892326344, 2);
