INSERT INTO `t_topic` VALUES (1438886831839039489, 'admin', 1, '“三寸粉笔，三尺讲台系国运；一颗丹心，一生秉烛铸民魂。”这是习近平总书记对教师职业的高度评价。教师（　　）\n①是人类文明的主要传承者之一\n②承担着教书育人的使命\n③是履行教育教学的专业人员\n④将逐渐被网络所取代', '[]', '[{\"A\":{\"content\":\"①②④\",\"imageList\":[]}},{\"B\":{\"content\":\"②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②③\",\"imageList\":[]}},{\"D\":{\"content\":\"①③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '永州', 1631892328593, 1631892328593, 3);
