INSERT INTO `t_topic` VALUES (1438886832359133186, 'admin', 1, '浙江衢州女孩蓝微父亲离世，母亲瘫痪，自小学5年级起，她就在学校附近租房，边读书边照顾妈妈。今年大二的她，已经带妈妈读书近10年。对这则先进事迹材料拟定的标题，最恰当的是（　　）', '[]', '[{\"A\":{\"content\":\"尽孝只在磨难中\",\"imageList\":[]}},{\"B\":{\"content\":\"尽孝事事顺父母\",\"imageList\":[]}},{\"C\":{\"content\":\"孝敬父母在当下\",\"imageList\":[]}},{\"D\":{\"content\":\"家庭氛围要民主\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '瑶海区', 1631892328715, 1631892328715, 3);
