INSERT INTO `t_topic` VALUES (1438886834615668738, 'admin', 1, '客运站里旅客摩肩接踵，高速公路上“摩托大军”浩浩荡荡……2019年春运，约29.9亿人次陆续踏上旅途，汇聚成“流动的中国”。无数个回家的故事能够帮助我们理解（　　）\n①每个家庭的亲情表现不尽相同\n②家是我们心灵的港湾\n③每个人的内心都有一份对家人割舍不断的情感\n④中国的家庭结构发生了变化', '[]', '[{\"A\":{\"content\":\"①④\",\"imageList\":[]}},{\"B\":{\"content\":\"②③\",\"imageList\":[]}},{\"C\":{\"content\":\"②④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '孟村县', 1631892329264, 1631892329264, 3);
