INSERT INTO `t_topic` VALUES (1438886834871521281, 'admin', 1, '“百善孝为先”，为弘扬中华民族的优秀传统，推进社会主义核心价值观建设，近年来，全国各地先后出现了一批批“最美孝心少年”。他们的事迹告诉我们，孝敬父母就要（　　）\n①学会倾听，与父母保持亲近的关系\n②听从父母的一切教导\n③学会尊重、理解、关心、体谅父母\n④努力学习，感恩父母', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '射洪县', 1631892329313, 1631892329313, 3);
INSERT INTO `t_topic` VALUES (1438886834871521282, 'admin', 1, '家庭是社会的细胞，它的确立主要是基于（　　）\n①地域关系\n②婚姻关系\n③血缘关系\n④收养关系', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '萧山区', 1631892329320, 1631892329320, 3);
INSERT INTO `t_topic` VALUES (1438886834871521283, 'admin', 1, '习近平说：“今天的学生就是未来实现中华民族伟大复兴中国梦的主力军，广大教师就是打造这支中华民族‘梦之队’的筑梦人。”对此，下列理解正确的是（　　）\n①是对教师重要地位的充分肯定\n②这说明教师的职责就是为学生圆梦\n③这说明 教师在学生成长的过程中发挥着非常重要的作用\n④这启示我们要热爱教师、尊重教师', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '汾阳市', 1631892329326, 1631892329326, 3);
INSERT INTO `t_topic` VALUES (1438886834938630146, 'admin', 1, '孝亲敬长是中华民族的传统美德，也是每个中国公民的法律义务。作为中学生孝敬父母应该（　　）', '[]', '[{\"A\":{\"content\":\"打工挣钱，回报父母的养育之恩\",\"imageList\":[]}},{\"B\":{\"content\":\"认真学习，感恩于父母的辛劳\",\"imageList\":[]}},{\"C\":{\"content\":\"放弃学业，回家承担家务\",\"imageList\":[]}},{\"D\":{\"content\":\"一切按父母的要求去做\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '曲靖期', 1631892329331, 1631892329331, 3);
INSERT INTO `t_topic` VALUES (1438886834938630147, 'admin', 1, '爱是需要呵护的，亲子之间的相处是需要润滑剂的，下列有助于缓解亲子冲突的做法是（　　）', '[]', '[{\"A\":{\"content\":\"子女百依百顺，听从父母的\",\"imageList\":[]}},{\"B\":{\"content\":\"父母对子女尽量多的溺爱\",\"imageList\":[]}},{\"C\":{\"content\":\"和父母沟通要注意把握时机和态度\",\"imageList\":[]}},{\"D\":{\"content\":\"只要自己认为是对的，坚持到底\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '保山', 1631892329337, 1631892329337, 3);
INSERT INTO `t_topic` VALUES (1438886834938630148, 'admin', 1, '下列可以确立家庭关系的是（　　）\n①小王和小李领取了结婚证\n②“十月怀胎，一朝分娩”，家庭中喜添小宝宝\n③小张的妈妈收养了一个妹妹\n④张大爷和李大爷在养老院住在一起', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"②③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①③④\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '保山', 1631892329343, 1631892329343, 3);
INSERT INTO `t_topic` VALUES (1438886835001544706, 'admin', 1, '小华家规定：由小华当家里的小小调解员和裁判，当爸爸妈妈发生争执或者矛盾冲突时，由他给爸爸妈妈亮“黄牌”，示意“休战”，爸爸妈妈必须遵守约定，这表明（　　）', '[]', '[{\"A\":{\"content\":\"家庭和睦主要靠子女的协调\",\"imageList\":[]}},{\"B\":{\"content\":\"不吵架的家庭是没有的\",\"imageList\":[]}},{\"C\":{\"content\":\"在家里子女说了算就会减少家庭矛盾\",\"imageList\":[]}},{\"D\":{\"content\":\"和谐家庭需要大家的努力维护\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '保山', 1631892329350, 1631892329350, 3);
INSERT INTO `t_topic` VALUES (1438886835001544707, 'admin', 1, '下列与老师相处的做法正确的是（　　）', '[]', '[{\"A\":{\"content\":\"老师上课写错了一个字，小飞私下悄悄帮老师纠正\",\"imageList\":[]}},{\"B\":{\"content\":\"小琪为了显示自己和历史张老师关系好，经常在教室里叫张老师张姐\",\"imageList\":[]}},{\"C\":{\"content\":\"张默数学作业不认真，老师批评了他，他心里暗暗下决心：再也不听数学课了\",\"imageList\":[]}},{\"D\":{\"content\":\"小刚有点怕老师，最近读到一篇很好的文章，但就是不敢和老师分享\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '曲靖', 1631892329355, 1631892329355, 3);
INSERT INTO `t_topic` VALUES (1438886835060264962, 'admin', 1, '“严慈相济”是一个有责任心的老师对待学生的基本态度，下列选项属于错误对待老师的表扬和批评的是（　　）', '[]', '[{\"A\":{\"content\":\"对老师的表扬要开心接受，对老师的批评可以怀恨在心\",\"imageList\":[]}},{\"B\":{\"content\":\"对老师批评，有则改之，无则加勉\",\"imageList\":[]}},{\"C\":{\"content\":\"老师的表扬和批评对我们都是一种爱\",\"imageList\":[]}},{\"D\":{\"content\":\"把老师的表扬当作鞭策，再接再厉\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '保山', 1631892329360, 1631892329360, 3);
INSERT INTO `t_topic` VALUES (1438886835060264963, 'admin', 1, '家里有亲人，家中有亲情。对于亲情，下列说法不正确的是（　　）', '[]', '[{\"A\":{\"content\":\"小明说：父母关心我的成绩，正是亲情的体现\",\"imageList\":[]}},{\"B\":{\"content\":\"张萌说：我们每一个人的内心都有一份对家人割舍不断地情感\",\"imageList\":[]}},{\"C\":{\"content\":\"刘敏说：每家有每家的特点，家庭的亲情表现不尽相同\",\"imageList\":[]}},{\"D\":{\"content\":\"朱浩说：爸爸妈妈经常吵架，我的家中没有亲情\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '曲靖', 1631892329366, 1631892329366, 3);
INSERT INTO `t_topic` VALUES (1438886835060264964, 'admin', 1, '对如图理解不正确的是（　　）', '[\"http://static.lzxtedu.com/7a2/3302.png\"]', '[{\"A\":{\"content\":\"孝亲敬长是中华民族的传统美德\",\"imageList\":[]}},{\"B\":{\"content\":\"孝亲敬长要落实到行动上，从小事做起\",\"imageList\":[]}},{\"C\":{\"content\":\"家长要给孩子树立一个孝亲敬长的好榜样\",\"imageList\":[]}},{\"D\":{\"content\":\"构建和谐家庭，要从根源上消除家庭矛盾\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '田家庵区', 1631892329372, 1631892329372, 3);
INSERT INTO `t_topic` VALUES (1438886835127373825, 'admin', 1, '近年来，在北京，名校博士毕业生应聘中小学教师比比皆是。深圳龙化区近30万年薪招聘中小学教师再度使教师一职成为社会热议话题。下列对教师的职业描述正确的是（　　）', '[]', '[{\"A\":{\"content\":\"师者，所以传道授业解惑也\",\"imageList\":[]}},{\"B\":{\"content\":\"时代在发展，对教师的要求没有变化\",\"imageList\":[]}},{\"C\":{\"content\":\"学生需要认真完成各项作业\",\"imageList\":[]}},{\"D\":{\"content\":\"教师是我们获取知识的唯一来源，理应高待遇\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '田家庵区', 1631892329377, 1631892329377, 3);
INSERT INTO `t_topic` VALUES (1438886835127373826, 'admin', 1, '我们的老师多才多艺，风趣幽默，既教我们知识，又教我们做人，我非常尊敬我们老师。尊敬老师是因为（　　）\n①老师是人类文明的主要传承者之一\n②老师承担教书育人的使命\n③他们是有理想信念、有道德情操、有扎实学识、有仁爱之心的好教师\n④老师是履行教育教学职责的专业人员', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"②③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②③④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '曲靖', 1631892329383, 1631892329383, 3);
INSERT INTO `t_topic` VALUES (1438886835194482690, 'admin', 1, '春运时拥挤的人群，或焦灼、或期盼、或喜悦的神情，背后流淌的是最具中国味的浓浓亲情，家始终是中国人内心深处的牵盼。对此，下列理解正确的是（　　）\n①家是我们身心的寄居之所，是我们心灵的港湾\n②家就是某个地域的一所房子\n③亲情让我们的心灵有所依靠\n④我们的成长离不开家庭的哺育和支持', '[]', '[{\"A\":{\"content\":\"②③④\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②③\",\"imageList\":[]}},{\"D\":{\"content\":\"①③④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '保山', 1631892329390, 1631892329390, 3);
INSERT INTO `t_topic` VALUES (1438886835194482691, 'admin', 1, '从教三十多年的张老师桃李满天下，在教学实践中越来越认同一句话--“教育的本质是唤醒，教育就是一棵树摇动另一棵树，一朵云推动另一朵云，一个灵魂唤醒另一个灵魂。”该材料主要体现了（　　）', '[]', '[{\"A\":{\"content\":\"师生之间是平等的关系，毫无差异\",\"imageList\":[]}},{\"B\":{\"content\":\"学生要感恩教师的付出，侍奉老师\",\"imageList\":[]}},{\"C\":{\"content\":\"教与学是师生相互促进，共同成长的过程\",\"imageList\":[]}},{\"D\":{\"content\":\"学生摆脱了对老师的依赖才能走向自主学习\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '田家庵区', 1631892329396, 1631892329396, 3);
INSERT INTO `t_topic` VALUES (1438886835194482692, 'admin', 1, '《礼记•学记》中说：“是故学然后知不足，教然后知困，知不足，然后能自反也；知困，然后能自强也。故曰，教学相长也”这段话告诉我们的道理是（　　）\n①学习离不开老师的引领和教导\n②我们与老师交流互动可以促进老师更好的教\n③教与学是师生共同成长、相互促进的过程\n④教与学是一样的', '[]', '[{\"A\":{\"content\":\"①②③④\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②③\",\"imageList\":[]}},{\"D\":{\"content\":\"①③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '保山', 1631892329402, 1631892329402, 3);
INSERT INTO `t_topic` VALUES (1438886835257397250, 'admin', 1, '电影《老师•好》口碑票房齐飞，它以师生情为切入点，讲述了老师与学生之间的感人 故事，也让学生明白老师严厉的教育是对学生的关爱和贵任。从师生关系的角度来说，下列说法不符合要求的是（　　）', '[]', '[{\"A\":{\"content\":\"教师是人类文明的传播者\",\"imageList\":[]}},{\"B\":{\"content\":\"师生之间应该相互理解，加强沟通\",\"imageList\":[]}},{\"C\":{\"content\":\"学生应该正确对待老师的批评\",\"imageList\":[]}},{\"D\":{\"content\":\"学生应该尊重不同风格的任课老师\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '禅城区', 1631892329407, 1631892329407, 3);
INSERT INTO `t_topic` VALUES (1438886835257397251, 'admin', 1, '爱心是学生打开知识之门、启迪心智的开始，爱心能够滋润浇开学生美丽的心灵之花。这句话说明老师要有（　　）', '[]', '[{\"A\":{\"content\":\"理想信念\",\"imageList\":[]}},{\"B\":{\"content\":\"道德情操\",\"imageList\":[]}},{\"C\":{\"content\":\"扎实学识\",\"imageList\":[]}},{\"D\":{\"content\":\"仁爱之心\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '孟村县', 1631892329413, 1631892329413, 3);
INSERT INTO `t_topic` VALUES (1438886835257397252, 'admin', 1, '七年级学生小华最近一段时间不愿与父母交流，有时候还顶撞父母。为了改善与父母的关系，他为自己制订了与父母沟通的计划。你认为正确的是（　　）', '[]', '[{\"A\":{\"content\":\"关心体贴父母，完全消除逆反心理\",\"imageList\":[]}},{\"B\":{\"content\":\"父母说的都是对的，就要事事听从父母的安排\",\"imageList\":[]}},{\"C\":{\"content\":\"为了让父母放心，他把数学考试30分的成绩说成是80分\",\"imageList\":[]}},{\"D\":{\"content\":\"当父母批评自己时，要认真倾听，心平气和地与父母沟通\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '双台子区', 1631892329419, 1631892329419, 3);
INSERT INTO `t_topic` VALUES (1438886835320311810, 'admin', 1, '自习课上，有同学嬉戏打闹传纸条，纸条正好落在小峰的桌子上，老师误以为是小峰在传纸条。当众批评了他，小峰感觉很委屈。对小峰缓解委屈有帮助的提示是（　　）\n①老师的批评意味着关心、提醒和劝诫\n②老师的批评可以帮助我们反省自己\n③给老师写个纸条说明当时的具体情况\n④老师的表扬意味着肯定、鼓励和期待', '[]', '[{\"A\":{\"content\":\"①②④\",\"imageList\":[]}},{\"B\":{\"content\":\"①②③\",\"imageList\":[]}},{\"C\":{\"content\":\"②④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②③④\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '•孟村县', 1631892329426, 1631892329426, 3);
INSERT INTO `t_topic` VALUES (1438886835320311811, 'admin', 1, '在中国的家庭文化中，“孝”是重要的精神内涵。下列选项中，有关“孝亲敬长”的说法不正确的是（　　）', '[]', '[{\"A\":{\"content\":\"孝亲敬长是中华民族传统美德\",\"imageList\":[]}},{\"B\":{\"content\":\"孝亲敬长是每个中国公民的法定义务\",\"imageList\":[]}},{\"C\":{\"content\":\"只要孝亲敬长必能让家更美好\",\"imageList\":[]}},{\"D\":{\"content\":\"孝亲敬长应体现在平时的一言一行中\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '江都区', 1631892329432, 1631892329432, 3);
INSERT INTO `t_topic` VALUES (1438886835387420674, 'admin', 1, '一个人遇到好老师是人生的幸运，一个学校拥有好老师是学校的光荣，一个民族源源不断地涌现出一批又一批好老师则是民族的希望。此观点的依据是（　　）\n①尊重老师，是中华民族的优良传统\n②老师是我们学习的指导者和成长的引路人\n③老师是人类文明的主要传承者之一\n④彼此尊重，是我们与老师建立良好关系的开始', '[]', '[{\"A\":{\"content\":\"①④\",\"imageList\":[]}},{\"B\":{\"content\":\"②③\",\"imageList\":[]}},{\"C\":{\"content\":\"②④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '江都区', 1631892329437, 1631892329437, 3);
INSERT INTO `t_topic` VALUES (1438886835387420675, 'admin', 1, '“城东百尺倚崔嵬，迢递长安载土来。一片夕阳春树绿，慈鸟飞绕定王台。”相传汉景帝时，长沙王刘发担忧远在长安的母亲却不能相见，于是派专人代为探母并将长安的泥土带回，年复一年，带回的泥土堆成了高台-定王台。从这个故事中，我们可以体会到（　　）\n①母子之间浓浓的骨肉亲情\n②孝亲敬长就要百依百顺\n③感恩之情要用行动来表达\n④“孝”的中华传统美德源远流长', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '孟村县', 1631892329443, 1631892329443, 3);
INSERT INTO `t_topic` VALUES (1438886835387420676, 'admin', 1, '“家是最小的国，国是千万家。”家庭的前途命运同国家和民族的前途命运紧密相连。中华民族自古以来就重视家庭、重视亲情。下列对家和亲情的认识错误的是（　　）', '[]', '[{\"A\":{\"content\":\"家既是我们身心的寄居之所，也是我们心灵的港湾\",\"imageList\":[]}},{\"B\":{\"content\":\"家具有经济、生育、情感交流、扶养、贍养等功能\",\"imageList\":[]}},{\"C\":{\"content\":\"家庭结构会改变，但亲情的表现形式不会发生变化\",\"imageList\":[]}},{\"D\":{\"content\":\"亲情是家庭的粘合剂，它能增进家庭成员的凝聚力\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '双台子', 1631892329449, 1631892329449, 3);
INSERT INTO `t_topic` VALUES (1438886835454529538, 'admin', 1, '我们班的道德与法治老师见解独到，能将深奥的道理通俗地讲解出来；数学老师教学严谨，常将习题用多种方法展示给学生；历史老师知识渊博，能在旁征博引中阐释历史事件……面对这些风格不同的老师，我们应该（　　）\n①发现不同风格老师的优点\n②承认不同风格老师的差别\n③只尊重我们喜欢的老师\n④主动与不同风格的老师交往', '[]', '[{\"A\":{\"content\":\"①③④\",\"imageList\":[]}},{\"B\":{\"content\":\"①②③\",\"imageList\":[]}},{\"C\":{\"content\":\"②③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '孟村县', 1631892329455, 1631892329455, 3);
INSERT INTO `t_topic` VALUES (1438886835454529539, 'admin', 1, '有许多心力交瘁的父母十分不解：为什么那么关爱孩子，反而造成亲子间那么大的冲突与鸿沟？有许多处于“成长的烦恼”中的孩子也很苦恼：为什么我的父母那么专制，管得那么多，总是想包办我的一切？针对上述情形，孩子和家长应（　　）', '[]', '[{\"A\":{\"content\":\"相互理解，积极沟通\",\"imageList\":[]}},{\"B\":{\"content\":\"坚持己见，冲突碰撞\",\"imageList\":[]}},{\"C\":{\"content\":\"顺其自然，各行其是\",\"imageList\":[]}},{\"D\":{\"content\":\"换位思考，减少交流\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '锡山区', 1631892329462, 1631892329462, 3);
INSERT INTO `t_topic` VALUES (1438886835521638402, 'admin', 1, '生命是一场旅行，老师是陪伴我们“旅行”的重要他人。下列名言中赞美老师的有（　　）\n①谁言寸草心，报得三春晖\n②春蚕到死丝方尽，蜡炬成灰泪始于\n③捧着一颗心来，不带半根草去\n④一腔热血育桃李，三尺讲坛写春秋', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"②③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '蚌埠', 1631892329467, 1631892329467, 3);
INSERT INTO `t_topic` VALUES (1438886835521638403, 'admin', 1, '关于回家的心情是怎样的，漫画中的人物的回答是“回家真好！”。你认为“回家真好”的理由是（　　）\n①家是我们心灵的港湾\n②家庭是由婚姻关系、血缘关系或收养关系结合成的亲属生活组织\n③家里有温暖的牵挂、真切的关怀和守候\n④家是我们的身心寄居之所', '[\"http://static.lzxtedu.com/7a2/3319.png\"]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '锡山区', 1631892329474, 1631892329474, 3);
INSERT INTO `t_topic` VALUES (1438886835521638404, 'admin', 1, '在学习论坛上，大家围绕“爱在家人间”的话题纷纷发言，表达自己的看法，以下观点中，你认同的是（　　）\n①每个家庭的亲情表现不尽相同\n②家庭结构发生改变之后，家中的亲情就不存在了\n③家是甜蜜、温暖、轻松的避风港，每个人对家人都有一份割舍不断的情感\n④感受不到自己渴望的亲情，说明亲情已经消失', '[]', '[{\"A\":{\"content\":\"①②\",\"imageList\":[]}},{\"B\":{\"content\":\"①③\",\"imageList\":[]}},{\"C\":{\"content\":\"①④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '惠城区', 1631892329479, 1631892329479, 3);
INSERT INTO `t_topic` VALUES (1438886835588747266, 'admin', 1, '“最美孝心少年”高雨欣，小小年纪就主动分担家庭重任。白天，她认真读书；晚上，帮妈妈烧烤……在家庭情况非常艰苦的情况下，她的成绩依然保持年年优秀。对此，下列说法不正确的是（　　）', '[]', '[{\"A\":{\"content\":\"青少年应该增强责任意识，主动分担家庭责任\",\"imageList\":[]}},{\"B\":{\"content\":\"孝敬父母只要成绩好就行，其它次要\",\"imageList\":[]}},{\"C\":{\"content\":\"孝敬父母就要体贴父母的辛劳，分担父母的忧愁\",\"imageList\":[]}},{\"D\":{\"content\":\"珍惜受教育权利，自觉履行受教育义务\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '惠城区', 1631892329485, 1631892329485, 3);
INSERT INTO `t_topic` VALUES (1438886835588747267, 'admin', 1, '《中华人民共和国宪法》规定：成年子女有赡养扶助父母的义务。对此认识正确的是（　　）', '[]', '[{\"A\":{\"content\":\"孝敬父母是我们长大成人之后的事情\",\"imageList\":[]}},{\"B\":{\"content\":\"孝敬父母是每个中国公民的法定义务\",\"imageList\":[]}},{\"C\":{\"content\":\"未成年人不具备孝敬父母的能力\",\"imageList\":[]}},{\"D\":{\"content\":\"孝敬父母最注重给予父母物质上的享受\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '灌云县', 1631892329491, 1631892329491, 3);
INSERT INTO `t_topic` VALUES (1438886835588747268, 'admin', 1, '“国将兴，必贵师而重傅：贵师而重傅，则法度存”出自《荀子•大略》强调“贵师而重傅”的原因是（　　）\n①人类文明都靠教育来传承\n②教师是我们知识学习的指导者\n③教师是我们精神成长的引路人\n④接纳不同风格的老师', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"②③\",\"imageList\":[]}},{\"D\":{\"content\":\"①④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '高阳县', 1631892329497, 1631892329497, 3);
INSERT INTO `t_topic` VALUES (1438886835655856129, 'admin', 1, '小文在作文中写道：“妈妈是我的好朋友，我伤心时，她安慰我，逗我开心。我有不会做的作业，她总是耐心给我讲解，直到我听懂了她才放心；爸爸是我的好伙伴，他常常陪我下棋、打羽毛球：家里的事情也是我们三个一起商量着解决。我有一个幸福的家！”据此可知（　　）\n①家中有亲情\n②我们的成长离不开家庭的支持\n③小文的家庭是个主干家庭\n④现代家庭都很平等、民主', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"②④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '高阳县', 1631892329503, 1631892329503, 3);
INSERT INTO `t_topic` VALUES (1438886835655856130, 'admin', 1, '下列对孝敬父母理解正确的是（　　）', '[]', '[{\"A\":{\"content\":\"孝敬父母会加重负担，得不偿失\",\"imageList\":[]}},{\"B\":{\"content\":\"孝敬父母只要在物质上帮助就可以\",\"imageList\":[]}},{\"C\":{\"content\":\"孝敬父母只要在精神上安慰就可以\",\"imageList\":[]}},{\"D\":{\"content\":\"孝敬父母要体现在实际行动上\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '泰兴市', 1631892329509, 1631892329509, 3);
INSERT INTO `t_topic` VALUES (1438886835718770690, 'admin', 1, '2019年央视春晚歌曲《妈，我回来啦》情感真挚，催人泪下。“有你的地方才是温暖的家”等歌词表达了（　　）\n①游子归家的热切之情\n②家是血脉相连的生活共同体\n③亲情之爱割舍不断的情感\n④家是甜蜜、温暖、轻松的避风港', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②③④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '颍州区', 1631892329515, 1631892329515, 3);
INSERT INTO `t_topic` VALUES (1438886835718770691, 'admin', 1, '师生之间难免有矛盾，化解与老师的误解、矛盾，增进与老师的感情，一切从主动沟通开始。以下四位同学与老师交换意见的语言，比较恰当的是 （　　）\n①小利：“老师，这个问题，我认为是不是应该……”\n②小文：“老师，您讲错了，压根就不是那么回事，应该……”\n③小美：“老师，我认为……，当然我讲的也不一定对……”\n④小詹：“老师，我说的一点都没错，就是您错了！”', '[]', '[{\"A\":{\"content\":\"①②\",\"imageList\":[]}},{\"B\":{\"content\":\"①③\",\"imageList\":[]}},{\"C\":{\"content\":\"①④\",\"imageList\":[]}},{\"D\":{\"content\":\"②④\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '东城区', 1631892329521, 1631892329521, 3);
INSERT INTO `t_topic` VALUES (1438886835718770692, 'admin', 1, '有人问诺贝尔经济学奖获得者卢卡斯：“芝加哥大学获得诺贝尔奖的教授很多，是否因为芝加哥大学的教授更有实力？”他回答说：“因为芝加哥大学的学生更有实力，而对这些有实力的学生，芝加哥大学的教授不得不更努力。”对此理解正确的是（　　）', '[]', '[{\"A\":{\"content\":\"师生之间是一种平等尊重的关系\",\"imageList\":[]}},{\"B\":{\"content\":\"芝加哥大学的学生比教授厉害\",\"imageList\":[]}},{\"C\":{\"content\":\"师生之间是一种教学相长的关系\",\"imageList\":[]}},{\"D\":{\"content\":\"学生要主动参与、勤学好问地跟老师沟通\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '从化区', 1631892329527, 1631892329527, 3);
INSERT INTO `t_topic` VALUES (1438886835785879554, 'admin', 1, '2018年教师节，习近平总书记发表重要讲话。他指出：“建设社会主义现代化强国，对教师队伍建设提出新的更高要求，也对全党全社会尊师重教提出新的更高要求。”这告诉我们（　　）', '[]', '[{\"A\":{\"content\":\"教师榜样示范作用不可替代\",\"imageList\":[]}},{\"B\":{\"content\":\"我国教育事业获得巨大发展\",\"imageList\":[]}},{\"C\":{\"content\":\"教书育人职责必将发生改变\",\"imageList\":[]}},{\"D\":{\"content\":\"培养创新型人才归根到底靠教育\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '大埔县', 1631892329532, 1631892329532, 3);
INSERT INTO `t_topic` VALUES (1438886835785879555, 'admin', 1, '随着社会的发展，现代家庭在结构、规模、观念等都发生了改变，下列说法不符合这种变化的是（　　）', '[]', '[{\"A\":{\"content\":\"家庭结构不断趋向小型化\",\"imageList\":[]}},{\"B\":{\"content\":\"家庭氛围越来越平等民主\",\"imageList\":[]}},{\"C\":{\"content\":\"由核心家庭向四代同堂转变\",\"imageList\":[]}},{\"D\":{\"content\":\"家庭成员之间的沟通方式多样化\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '从化区', 1631892329539, 1631892329539, 3);
INSERT INTO `t_topic` VALUES (1438886835785879556, 'admin', 1, '2019年11月22日，教育部公布了《中小学教师实施教育惩戒规则（征求意见稿）》向全社会征求意见，引发了广泛讨论。下列观点你不赞同的是（　　）', '[]', '[{\"A\":{\"content\":\"教育惩戒权立法有利于促进教师严格管理要求学生\",\"imageList\":[]}},{\"B\":{\"content\":\"这一定会伤害学生的身心健康，给学生造成痛苦\",\"imageList\":[]}},{\"C\":{\"content\":\"老师可以合理合法地管理和惩罚学生，但不能随意体罚\",\"imageList\":[]}},{\"D\":{\"content\":\"学生要把注意力放在老师批评和惩戒的用意上，理解老师的良苦用心\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '从化区', 1631892329544, 1631892329544, 3);
INSERT INTO `t_topic` VALUES (1438886835852988417, 'admin', 1, '“是故学然后知不足，教然后知困。知不足，然后能自反也；知困，然后能自强也。故曰；教学相长也。”对于《礼记•学记》中的这句话，下列理解正确的是（　　）\n①教与学是师生相互陪伴、相互促进、共同成长的过程\n②学生学习与老师教学，是两个互相独立的过程\n③我们的学习离不开老师的引领和指导\n④我们与老师的交流互动，可以促进老师更好地“教”\n', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '南昌', 1631892329550, 1631892329550, 3);
INSERT INTO `t_topic` VALUES (1438886835852988418, 'admin', 1, '我国的《宪法》《婚姻法》《老年人权益保护法》都有孝亲徽长的法律规定。对此，青少年应该（　　）\n①认识到孝亲敬长是每个中国公民的法律义务\n②只要知道孝亲敬长是中华民族的传统美德就够了\n③不仅要孝敬父母，还要孝敬父母的双亲等长辈\n④尽孝在当下，用实际行动表达我们的孝敬之心', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '东城区', 1631892329555, 1631892329555, 3);
INSERT INTO `t_topic` VALUES (1438886835911708674, 'admin', 1, '在我国，由祖辈、父辈、子辈组成的主干家庭不在少数，家和万事兴，但在温暖的大家庭中也会因各种原因而产生一些矛盾。建设和谐家庭，我们应该（　　）\n①学会在祖辈和父母间选边站\n②相互理解、信任、体谅和包容\n③对家庭成员的“甜蜜碰撞”视而不见\n④增强责任意识，主动承担力所能及的家务\n', '[]', '[{\"A\":{\"content\":\"①③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②\",\"imageList\":[]}},{\"C\":{\"content\":\"③④\",\"imageList\":[]}},{\"D\":{\"content\":\"②④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '惠安县', 1631892329561, 1631892329561, 3);
INSERT INTO `t_topic` VALUES (1438886835911708675, 'admin', 1, '2019年12月12日，2020年春运火车票开售。春运从1月10日开始，2月18日结束，全国铁路预计发送旅客4.4亿人次。“有钱没钱，回家过年”，我们热爱自己的家是因为（　　）', '[]', '[{\"A\":{\"content\":\"家庭成员间没有矛盾和冲突\",\"imageList\":[]}},{\"B\":{\"content\":\"家里有亲人，家中有亲情\",\"imageList\":[]}},{\"C\":{\"content\":\"家中有优越的物质生活条件\",\"imageList\":[]}},{\"D\":{\"content\":\"在家中可以衣来伸手，饭来张口\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '长垣县', 1631892329566, 1631892329566, 3);
