INSERT INTO `t_topic` VALUES (1438886836175949826, 'admin', 1, '家庭相册是打开昔日回忆的钥匙，是见证家庭发展的温馨记忆。“有家人在，有故事讲，有相册翻”，央视邀请网友进行“我拍中国年”活动，欢乐的瞬间，都会定格在一张张照片的笑脸上，你的内心情感与这则新闻最吻合的是（　　）', '[]', '[{\"A\":{\"content\":\"春节只要家人乐，道德礼俗全不管\",\"imageList\":[]}},{\"B\":{\"content\":\"亲人团圆庆新春，欢乐搞笑无所忌\",\"imageList\":[]}},{\"C\":{\"content\":\"家人之间好故事，相册翻出和谐年\",\"imageList\":[]}},{\"D\":{\"content\":\"不负光阴不负卿，新的一年再出发\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '福清市', 1631892329626, 1631892329626, 3);
