INSERT INTO `t_topic` VALUES (1438886836310167556, 'admin', 1, '“我想要走进你的世界，你不欢迎我；期待你走进我的世界，你又不来。”这是一位母亲的难言与无奈。青春期的逆反心理在一定程度上影响了与父母的交流，对此我们应该（　　）\n①端正自己的态度，理解父母的苦心\n②听父母的话绝不违背父母的意愿\n③创造机会，多与父母交流沟通\n④孝敬父母，不做父母反对的事情\n', '[]', '[{\"A\":{\"content\":\"②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③\",\"imageList\":[]}},{\"D\":{\"content\":\"②④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '福清市', 1631892329671, 1631892329671, 3);
INSERT INTO `t_topic` VALUES (1438886836377276417, 'admin', 1, '我国古代长期流传着“养不教，父之过”、“望子成龙”、“孟母三迁”、“岳母刺字”、“孔融让 梨”等故事或谚语。这些表明（　　）', '[]', '[{\"A\":{\"content\":\"在家教方面，古人有很多成功经验，但现在已经全部过时了\",\"imageList\":[]}},{\"B\":{\"content\":\"中华民族是个重视家庭教育的民族\",\"imageList\":[]}},{\"C\":{\"content\":\"“家”的观念在中华民族的观念中根深蒂固\",\"imageList\":[]}},{\"D\":{\"content\":\"我们要完全吸收、学习古人家庭教育的经验\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '大通区', 1631892329676, 1631892329676, 3);
INSERT INTO `t_topic` VALUES (1438886836377276418, 'admin', 1, '中学的各科老师风格迥异，张华只喜欢数学老师、生物老师的讲课方式。张华应当（　　）', '[]', '[{\"A\":{\"content\":\"接纳、尊重每位老师的“不同”\",\"imageList\":[]}},{\"B\":{\"content\":\"拒绝和不喜欢的老师主动交往\",\"imageList\":[]}},{\"C\":{\"content\":\"只接纳、尊重自己喜欢的老师\",\"imageList\":[]}},{\"D\":{\"content\":\"对不喜欢的老师直接提出批评\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '仪征市', 1631892329682, 1631892329682, 3);
