INSERT INTO `t_topic` VALUES (1438886836444385284, 'admin', 1, '尊师重教，要求我们青少年（　　）', '[]', '[{\"A\":{\"content\":\"不给老师提意见，对老师敬而远之\",\"imageList\":[]}},{\"B\":{\"content\":\"接受老师的表扬，不接受老师的批评\",\"imageList\":[]}},{\"C\":{\"content\":\"对老师唯命是从，绝对服从老师的安排\",\"imageList\":[]}},{\"D\":{\"content\":\"尊重老师的劳动，主动与老师沟通\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '东台市', 1631892329699, 1631892329699, 3);
INSERT INTO `t_topic` VALUES (1438886836507299842, 'admin', 1, '你认为如图漫画《爸妈吵架你站哪一边？》中的孩子不应该（　　）', '[\"http://static.lzxtedu.com/7a2/3358.png\"]', '[{\"A\":{\"content\":\"躲在自己的房间里想想到底该站在哪边\",\"imageList\":[]}},{\"B\":{\"content\":\"帮助爸妈舒缓情绪，当他们的倾听者\",\"imageList\":[]}},{\"C\":{\"content\":\"把握时机，引导他们看到对方的优点\",\"imageList\":[]}},{\"D\":{\"content\":\"充当“黏合剂”，帮助他们走出“面子”的困境\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '盐田区', 1631892329705, 1631892329705, 3);
INSERT INTO `t_topic` VALUES (1438886836507299843, 'admin', 1, '师生交往是学校生活的重要内容，下列不属于“亦师亦友”的师生关系的是（　　）', '[]', '[{\"A\":{\"content\":\"师生共同创建班级博客\",\"imageList\":[]}},{\"B\":{\"content\":\"小刚不服刘老师的批评，顶撞刘老师\",\"imageList\":[]}},{\"C\":{\"content\":\"晚会上，王老师和小丽表演的哑剧让同学们好开心\",\"imageList\":[]}},{\"D\":{\"content\":\"放学后，小敏和张老师交流自己与同学交往中的烦心事\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '兴化市', 1631892329712, 1631892329712, 3);
INSERT INTO `t_topic` VALUES (1438886836507299844, 'admin', 1, '2019年3月18日，在学校思想政治理论课教师座谈会上，习近平总书记勉励教师“给学生心灵埋下真善美的种子，引导学生扣好人生第一粒扣子。”下面观点正确的是（　　）\n①老师是我们学习的指导者\n②老师是我们成长的引路人\n③老师承担着教书育人的使命\n④学生成才主要依靠老师', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '望江县', 1631892329717, 1631892329717, 3);
INSERT INTO `t_topic` VALUES (1438886836574408706, 'admin', 1, '家是一生读不完的书，家是心灵的港湾……以下关于家的说法正确的是（　　）\n①家里有亲人，家中有亲情\n②家是我们身心的寄居之所\n③家能满足我们所有的需求\n④家在所有人的心中都是甜蜜、温暖的地方', '[]', '[{\"A\":{\"content\":\"①②\",\"imageList\":[]}},{\"B\":{\"content\":\"②④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③\",\"imageList\":[]}},{\"D\":{\"content\":\"②③\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '颍东区', 1631892329723, 1631892329723, 3);
INSERT INTO `t_topic` VALUES (1438886836574408707, 'admin', 1, '“孝心美德少年”邓天芦，从三岁起开始学习照顾生活不能自理的妈妈。多年来，他一直坚持为妈妈洗衣做饭，同时还管理自家农田和菜地。他的事变表明（　　）', '[]', '[{\"A\":{\"content\":\"孝敬父母就是在生活上照顾父母\",\"imageList\":[]}},{\"B\":{\"content\":\"孝敬父母要在物质上赡养父母\",\"imageList\":[]}},{\"C\":{\"content\":\"孝敬父母是中华民族的传统美德\",\"imageList\":[]}},{\"D\":{\"content\":\"孝敬父母只是公民的道德义务\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '东台市', 1631892329729, 1631892329729, 3);
INSERT INTO `t_topic` VALUES (1438886836637323265, 'admin', 1, '如图，当父母之间发生矛盾或冲突时，我们应该（　　）\n①帮助父母舒缓情绪\n②明确自己是不偏不倚的中立者\n③引导父母看到对方优点\n④帮助父母走出“面子”困境', '[\"http://static.lzxtedu.com/7a2/3362.png\"]', '[{\"A\":{\"content\":\"①③④\",\"imageList\":[]}},{\"B\":{\"content\":\"①②③\",\"imageList\":[]}},{\"C\":{\"content\":\"①②③④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '兴化市', 1631892329735, 1631892329735, 3);
INSERT INTO `t_topic` VALUES (1438886836637323266, 'admin', 1, '古人云：“修身、齐家、平天下。”习近平说：“家庭是社会的基本细胞，是人生的第一所学校。”弘扬中华民族家庭美德、树立良好家风，关系到家庭和睦，关系到社会和谐，关系到下一代健康成长。下列理解最正确的是（　　）', '[]', '[{\"A\":{\"content\":\"家是代代相传，血脉相连的生活共同体\",\"imageList\":[]}},{\"B\":{\"content\":\"家风是我们立身做人的行为准则，是社会和谐的基础\",\"imageList\":[]}},{\"C\":{\"content\":\"家里有父母的牵挂，激励我们奋斗拼搏\",\"imageList\":[]}},{\"D\":{\"content\":\"家能满足个人所有的需要，是心灵的避风港\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '沙依巴克区', 1631892329741, 1631892329741, 3);
INSERT INTO `t_topic` VALUES (1438886836637323267, 'admin', 1, '中央电视台和光明日报社共同主办的大型公益活动“寻找最美乡村教师”用真挚的情感，真实记录乡村教师无私奉献、甘为人梯的精神品格。这体现了（　　）\n①作为教育工作者，教师是人类文明的传承者\n②教师肩负着教书育人的神圣职责\n③教师对人类文明做的贡献值得我们尊敬\n④乡村教师比城镇教师更优秀', '[]', '[{\"A\":{\"content\":\"①②④\",\"imageList\":[]}},{\"B\":{\"content\":\"②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②③\",\"imageList\":[]}},{\"D\":{\"content\":\"①③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '市中区', 1631892329746, 1631892329746, 3);
INSERT INTO `t_topic` VALUES (1438886836700237825, 'admin', 1, '临近七年级期末考试，妈妈禁止小军上网玩游戏，为此小军大发脾气。下列对小军行为的认识正确的有（　　）\n①小军误解了妈妈对自己的亲情\n②小军合理地发泄了自己的情绪\n③小军应该尊重妈妈，把握行为底线\n④小军渴望独立，有权拒绝父母干涉他的生活', '[]', '[{\"A\":{\"content\":\"①②\",\"imageList\":[]}},{\"B\":{\"content\":\"②④\",\"imageList\":[]}},{\"C\":{\"content\":\"②③\",\"imageList\":[]}},{\"D\":{\"content\":\"①③\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '广丰区', 1631892329752, 1631892329752, 3);
INSERT INTO `t_topic` VALUES (1438886836700237826, 'admin', 1, '根据如图漫画，你认为最合适的做法是（　　）', '[\"http://static.lzxtedu.com/7a2/3367.png\"]', '[{\"A\":{\"content\":\"多陪长辈聊天\",\"imageList\":[]}},{\"B\":{\"content\":\"打开电视让长辈看\",\"imageList\":[]}},{\"C\":{\"content\":\"购买数码产品给长辈\",\"imageList\":[]}},{\"D\":{\"content\":\"提醒长辈，多注意休息\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '奉化区', 1631892329758, 1631892329758, 3);
INSERT INTO `t_topic` VALUES (1438886836700237827, 'admin', 1, '子贡是孔子的优秀学生，却经常受到孔子严厉、苛刻的批评。子贡虚心接受，对孔子感恩戴德，遵从教诲，终于成为有大作为的贤人。下列说法中符合上述材料的是（　　）\n①老师真诚的批评是爱护、期待\n②对待学生，老师要多批评，少鼓励\n③学生要正确对待老师的批评，有则改之，无则加勉\n④良药苦口利于病，忠言逆耳利于行', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '市中区', 1631892329764, 1631892329764, 3);
INSERT INTO `t_topic` VALUES (1438886836767346689, 'admin', 1, '2020年1月10日一年一度的春运大幕正式拉起，今年春运全国预计旅客发送量将达到约30亿人次。春运的背后，流淌的是最具有中国味的浓浓亲情。对此认识正确的是（　　）\n①家中有亲人，家中有亲情，让我们的心灵有所依靠\n②家只是我们身心的寄居之所，我们的成长离不开家庭的哺育和支持\n③家是代代相传、血脉相连的生活共同体\n④家是甜蜜、温暖、轻松的避风港', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①③④\",\"imageList\":[]}},{\"C\":{\"content\":\"②③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②④\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '东台市', 1631892329770, 1631892329770, 3);
INSERT INTO `t_topic` VALUES (1438886836767346690, 'admin', 1, '家庭是影响人的一个极为重要的社会化因素。从人们接受教育的顺序来看，首先接受家庭环境的影响，然后才是幼儿园、学校的影响。这说明（　　）\n①人的成长阶段中，最重要的是幼儿时期\n②人的成长是不断社会化的过程，其中家庭的关爱和教育不可缺失\n③父母的抚育有助于人的社会化，使我们逐步成长为一名合格的社会成员\n④人的生存和发展过程中，只有从家庭中才能获得物质支持和精神滋养', '[]', '[{\"A\":{\"content\":\"①②\",\"imageList\":[]}},{\"B\":{\"content\":\"①③\",\"imageList\":[]}},{\"C\":{\"content\":\"②③\",\"imageList\":[]}},{\"D\":{\"content\":\"③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '沙依巴克区', 1631892329776, 1631892329776, 3);
INSERT INTO `t_topic` VALUES (1438886836830261249, 'admin', 1, '“鸟有反哺之情，羊有跪乳之恩。”这句民谚给我们在家庭生活中的启示有（　　）\n①用行动表达对双亲和长辈的孝敬之心\n②孝敬父母是我们的天职，要从小养成孝敬父母的好习惯\n③父母在任何时候都应当养育子女\n④我们要继承和弘扬中华民族孝亲敬长的优良传统', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '市中区', 1631892329781, 1631892329781, 3);
INSERT INTO `t_topic` VALUES (1438886837153222658, 'admin', 1, '十二月的一天，突然降温，妈妈一定要小强穿上厚外套，小强执意不肯，觉得妈妈很唠叨，小强要出家门时，妈妈还是拿着外套让他穿……．遇到这种情景，你会（　　）\n①感受和体会与父母相处的那些瞬间\n②通过良好的互动沟通来解决问题\n③学会理解和尊重父母的良苦用心\n④不伤害父母感情和不影响亲子关系', '[]', '[{\"A\":{\"content\":\"①②④\",\"imageList\":[]}},{\"B\":{\"content\":\"①③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②③\",\"imageList\":[]}},{\"D\":{\"content\":\"①②③④\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '滨城区', 1631892329788, 1631892329788, 3);
INSERT INTO `t_topic` VALUES (1438886837153222659, 'admin', 1, '学生乐于学习，老师寓教于乐，师生彼此尊重、相互关心、携手共进，这是师生交往的良好状态，我们与老师朝夕相处，发生矛盾和分歧在所难免，下列做法中不正确的是（　　）\n①针锋相对，据理力争\n②敬而远之，避免冲突\n③坦诚相待，主动沟通\n④怀恨在心，不理不睬', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '东台市', 1631892329863, 1631892329863, 3);
INSERT INTO `t_topic` VALUES (1438886837153222660, 'admin', 1, '回望2019，不论是平淡日子里的默默坚守，还是危机时刻的挺身而出，老师给我们带来太多温暖，太多感动；地震中，为疏散学生熊文燕老师冲向教室；路牌砸来，为保护学生苏敏老师勇敢迎上云；段建珉老师临终前捐献自己的眼角膜，用行动为学生们上了“最后一堂课”……材料告诉我们（　　）', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '新乡', 1631892329870, 1631892329870, 3);
INSERT INTO `t_topic` VALUES (1438886837216137217, 'admin', 1, '随着社会历史的演进，现代家庭发生了很多的变化，主要表现在（　　）\n①现代家庭的结构、规模、观念等发生了不同程度的变化\n②家庭成员之间的冲突越来越多\n③家庭成员的交流、沟通方式发生了较大的变化\n④家庭氛围越来越平等、民主', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '望江县', 1631892329876, 1631892329876, 3);
INSERT INTO `t_topic` VALUES (1438886837216137218, 'admin', 1, '时代在发展，教师的工作理念和工作方式发生了很大的变化，也对教师提出了更高的要求。今天，要成为一名好老师，必须具备（　　）\n①幽默风趣\n②有理想信念\n③有扎实学识\n④有道德情操', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"②③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①③④\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '颍东区', 1631892329882, 1631892329882, 3);
INSERT INTO `t_topic` VALUES (1438886837283246082, 'admin', 1, '对下边漫画认识正确的是（　　）', '[\"http://static.lzxtedu.com/7a2/3377.png\"]', '[{\"A\":{\"content\":\"老师这种古老的职业随着时代的发展而逐渐消亡\",\"imageList\":[]}},{\"B\":{\"content\":\"科技时代，老师的作用明显减小了\",\"imageList\":[]}},{\"C\":{\"content\":\"孩子完全可以在家通过网络自学成才\",\"imageList\":[]}},{\"D\":{\"content\":\"科技的发展进步不能取代老师在教书育人中的作用\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '沙依巴克区', 1631892329888, 1631892329888, 3);
INSERT INTO `t_topic` VALUES (1438886837283246083, 'admin', 1, '今年21岁的潘正江，现在是徐州工程学院机械设计专业大二的学生。去年，他做了一个决定--带着偏瘫的母亲上大学。潘正江要做的，除了学习之外。就是无微不至地照顾母亲，每天五次喂饭，给母亲翻身按摩十几遍。他说，“我不奢求妈妈能奇迹般地恢复，只要妈妈还在，只要每天回到家里能够看到妈妈就行。”潘正江的事迹诠释了（　　）', '[]', '[{\"A\":{\"content\":\"要告别依赖，保持乐观的心态\",\"imageList\":[]}},{\"B\":{\"content\":\"沟通是解决亲子冲突的最好方式\",\"imageList\":[]}},{\"C\":{\"content\":\"善于理解父母，用心体味亲情\",\"imageList\":[]}},{\"D\":{\"content\":\"孝敬双亲长辈要从现在开始\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '新乡', 1631892329894, 1631892329894, 3);
INSERT INTO `t_topic` VALUES (1438886837283246084, 'admin', 1, '孟子说：“天下之本在国，国之本在家。”意思是说，只有家庭关系和睦，才会有社会关系的和谐、顺畅。要实现和谐家庭，需要（　　）\n①勤俭持家，形成良好家风\n②家庭成员间要和睦相处\n③要共同分担家务，共同协作\n④要以良好的心态面对家庭发展中的变化', '[]', '[{\"A\":{\"content\":\"①②③④\",\"imageList\":[]}},{\"B\":{\"content\":\"①②③\",\"imageList\":[]}},{\"C\":{\"content\":\"①②④\",\"imageList\":[]}},{\"D\":{\"content\":\"①③④\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '东海县', 1631892329900, 1631892329900, 3);
INSERT INTO `t_topic` VALUES (1438886837350354946, 'admin', 1, '在中国的家庭文化中，“孝”是重要的精神内涵。因为孝敬父母是（　　）\n①道德规范的要求\n②公民的法律义务\n③中国传统美德\n④人际交往的技巧', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"②③④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③④\",\"imageList\":[]}},{\"D\":{\"content\":\"①②④\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '常熟市', 1631892329905, 1631892329905, 3);
INSERT INTO `t_topic` VALUES (1438886837350354947, 'admin', 1, '2019年12月12日，2020年春运火车票正式开抢。2020年春运期间，全国铁路预计发送旅客4.4亿人次，一票难求的困境难以阻挡务工者回家的念头。 务工者不辞辛苦在寒冷日子里坚定回家，体现了（　　）', '[]', '[{\"A\":{\"content\":\"家庭的支持是个人成长的重要动力\",\"imageList\":[]}},{\"B\":{\"content\":\"家通过亲情之爱承载人的心灵\",\"imageList\":[]}},{\"C\":{\"content\":\"家庭成员之间都由血缘关系连接\",\"imageList\":[]}},{\"D\":{\"content\":\"家庭不需要通过房屋和地域体现其价值\",\"imageList\":[]}}]', 'B', '', 8, 1, '', '高明区', 1631892329911, 1631892329911, 3);
