INSERT INTO `t_topic` VALUES (1438886840668049410, 'admin', 1, '某校在七年级开展了“经典诵读”、“宣纸制作”和“参观安徽省博物馆”等活动。这些活动的开展（　）\n①有利于在民族文化中滋养我们的精神生命\n②能激发我们弘扬民族精神，守护精神家园\n③有助于我们在精神上继承和创造文明成果\n④表明了爱护我们的身体是守护生命的体现\n', '[]', '[{\"A\":{\"content\":\"①②③\",\"imageList\":[]}},{\"B\":{\"content\":\"①②④\",\"imageList\":[]}},{\"C\":{\"content\":\"①③④\",\"imageList\":[]}},{\"D\":{\"content\":\"②③④\",\"imageList\":[]}}]', 'A', '', 8, 1, '', '包河区', 1631892330701, 1631892330701, 4);
