INSERT INTO `t_topic` VALUES (1438886843218186243, 'admin', 1, '礼让斑马线，反映了城市的文明程度。近年来，全国各个城市都在积极开展礼让斑马线活动，让行人在斑马线上走得更加从容。开展这一活动体现了（　　）', '[]', '[{\"A\":{\"content\":\"生命是脆弱的\",\"imageList\":[]}},{\"B\":{\"content\":\"生命是坚韧和顽强的\",\"imageList\":[]}},{\"C\":{\"content\":\"生命有接续\",\"imageList\":[]}},{\"D\":{\"content\":\"生命是宝贵的，要尊重、关爱生命\",\"imageList\":[]}}]', 'D', '', 8, 1, '', '张家港市', 1631892331316, 1631892331316, 4);
INSERT INTO `t_topic` VALUES (1438886843285295106, 'admin', 1, '社会上有一些志愿者填写《无偿捐献眼角膜志愿申请同意书》，自愿在逝世后无偿捐献自己的眼角膜。这一行为（　　）', '[]', '[{\"A\":{\"content\":\"是不尊重自己生命的行为\",\"imageList\":[]}},{\"B\":{\"content\":\"说明他人生命比自己更重要\",\"imageList\":[]}},{\"C\":{\"content\":\"体现珍爱生命、敬畏生命\",\"imageList\":[]}},{\"D\":{\"content\":\"说明生命来之不易、绚丽多彩\",\"imageList\":[]}}]', 'C', '', 8, 1, '', '柳州', 1631892331322, 1631892331322, 4);
